import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import apiClient from '../services/apiClient';
import './SocialMediaPage.css';

function FacebookPage() {
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');

  // Facebook OAuth states
  const [selectedPage, setSelectedPage] = useState(null);
const [availablePages, setAvailablePages] = useState([]); // Store all fetched pages
  const [facebookConnected, setFacebookConnected] = useState(false);

  // Auto-reply toggle state
  const [autoReplyEnabled, setAutoReplyEnabled] = useState(false);

  // Tab selection state
  const [activeTab, setActiveTab] = useState('auto');

  // Auto post states
  const [autoFormData, setAutoFormData] = useState({
    prompt: '',
    mediaType: 'none', // 'none', 'photo', 'video'
    mediaFile: null,
    generatedContent: '',
    isGenerating: false
  });

  // Manual post states
  const [manualFormData, setManualFormData] = useState({
    message: '',
    mediaType: 'none',
    mediaFile: null
  });

  // Publishing state
  const [isPublishing, setIsPublishing] = useState(false);

  // fetchPages function
  const fetchPages = useCallback(async (accessToken) => {
    console.debug('[fetchPages] Called with accessToken:', accessToken);
    console.debug('[fetchPages] Using dual apps:', USE_DUAL_APPS);

    if (!accessToken) {
      setConnectionStatus('❌ No Facebook access token found. Please try reconnecting.');
      setIsConnecting(false);
      setFacebookConnected(false);
      return { mappedPages: [], userInfo: null };
    }

    try {
      // If using dual apps, get pages access token via direct API call
      if (USE_DUAL_APPS) {
        console.debug('[fetchPages] Dual app mode - requesting pages access via direct API...');
        setConnectionStatus('🔄 Getting page permissions...');

        // For now, try using the original access token but with pages app API calls
        // This is a simplified approach that might work if the user has already granted permissions
        console.debug('[fetchPages] Using original access token for pages API calls');
        setConnectionStatus('🔄 Attempting to fetch pages with current permissions...');
      }
      // Check what permissions we actually have
      const permissionsResponse = await new Promise((resolve, reject) => {
        window.FB.api('/me/permissions', { access_token: accessToken }, (response) => {
          console.debug('[FB.api] /me/permissions response:', response);
          if (response.error) reject(new Error(response.error.message));
          else resolve(response);
        });
      });

      const grantedPermissions = permissionsResponse.data?.filter(p => p.status === 'granted').map(p => p.permission) || [];
      console.debug('[fetchPages] Granted permissions:', grantedPermissions);
      console.debug('[fetchPages] All permissions (including declined):', permissionsResponse.data);

      // In dual app mode, we expect basic permissions only
      if (USE_DUAL_APPS) {
        console.debug('[fetchPages] Dual app mode - checking if we have basic login permissions');
        if (grantedPermissions.includes('public_profile')) {
          console.debug('[fetchPages] Basic login successful, need to connect pages app');
          throw new Error('NEED_PAGES_AUTH'); // Trigger pages auth flow
        }
      } else {
        // Check for required permissions in single app mode
        const requiredPermissions = ['pages_show_list', 'pages_manage_posts'];
        const missingPermissions = requiredPermissions.filter(p => !grantedPermissions.includes(p));

        if (missingPermissions.length > 0) {
          setConnectionStatus(`❌ Missing permissions: ${missingPermissions.join(', ')}. Your app needs "Pages API" permissions.`);
          console.error('[fetchPages] Missing required permissions:', missingPermissions);
        }
      }

      // Get user info
      const userInfo = await new Promise((resolve, reject) => {
        window.FB.api('/me', { access_token: accessToken, fields: 'id,name,email' }, (response) => {
          console.debug('[FB.api] /me response:', response);
          if (response.error) reject(new Error(response.error.message));
          else resolve(response);
        });
      });

      // Get user's pages with extended permissions
      const pagesResponse = await new Promise((resolve, reject) => {
        window.FB.api('/me/accounts', {
          access_token: accessToken,
          fields: 'id,name,category,access_token,picture,fan_count,tasks'
        }, (response) => {
          // Debug log for troubleshooting
          console.debug('[FB.api] /me/accounts response:', response);
          console.debug('[FB.api] /me/accounts full response object:', JSON.stringify(response, null, 2));
          if (response.error) {
            console.error('[FB.api] /me/accounts error details:', response.error);
            reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
          } else {
            resolve(response);
          }
        });
      });
      const pages = pagesResponse.data || [];
      const mappedPages = pages.map(page => {
        // Determine permissions from tasks array
        const tasks = page.tasks || [];
        const canPost = tasks.includes('CREATE_CONTENT') || tasks.includes('MANAGE');
        const canComment = tasks.includes('MODERATE') || tasks.includes('MANAGE');

        return {
          id: page.id,
          name: page.name,
          category: page.category || 'Page',
          access_token: page.access_token,
          profilePicture: page.picture?.data?.url || '',
          canPost: canPost,
          canComment: canComment,
          followerCount: page.fan_count || 0,
          tasks: tasks // Keep tasks for debugging
        };
      });
      setAvailablePages(mappedPages);
      // Page selection logic
      if (mappedPages.length === 1) {
        setSelectedPage(mappedPages[0]);
        setConnectionStatus(`✅ Connected successfully! 1 page found.`);
      } else if (mappedPages.length > 1) {
        setSelectedPage(null); // Prompt user to select a page
        setConnectionStatus(`✅ Connected successfully! ${mappedPages.length} pages found. Please select a page below.`);
      } else {
        // Fallback to user profile if no pages available
        setSelectedPage({
          id: userInfo.id,
          name: userInfo.name,
          access_token: accessToken,
          category: 'Personal Profile',
          profilePicture: '',
          email: userInfo.email || '',
          isUserProfile: true,
          canPost: true,
          canComment: true,
          followerCount: 0
        });
        setConnectionStatus('✅ Connected as personal profile (no pages found).');
      }
      setFacebookConnected(true);
      setIsConnecting(false);
      return { mappedPages, userInfo };
          } catch (error) {
        console.error('Facebook API error:', error);

        // If dual app mode and we get the specific pages auth trigger
        if (USE_DUAL_APPS && (error.message === 'NEED_PAGES_AUTH' || error.message.includes('permissions'))) {
          console.debug('[fetchPages] Dual app mode - basic login successful, need pages auth');
          setConnectionStatus('✅ Basic login successful! Now connect your pages app for page management.');
          setIsConnecting(false);
          setFacebookConnected(true); // Mark as connected to show the pages auth button
          return { mappedPages: [], userInfo: null, needsPagesAuth: true };
        }

        // If dual app mode but other error, still try pages auth
        if (USE_DUAL_APPS) {
          console.debug('[fetchPages] Dual app mode - treating error as need for pages auth');
          setConnectionStatus('✅ Basic login successful! Pages app connection needed for page management.');
          setIsConnecting(false);
          setFacebookConnected(true); // Mark as connected to show the pages auth button
          return { mappedPages: [], userInfo: null, needsPagesAuth: true };
        }

        setConnectionStatus('❌ Failed to get Facebook data: ' + (error.message || 'Unknown error'));
        setIsConnecting(false);
        setFacebookConnected(false);
        return { mappedPages: [], userInfo: null };
      }
  }, [USE_DUAL_APPS]);

  // connectToBackend function
  const connectToBackend = async (accessToken, userInfo, mappedPages) => {
    try {
      console.debug('[connectToBackend] Connecting to backend with:', {
        userInfo: userInfo?.id,
        pagesCount: mappedPages.length,
        pagesData: mappedPages
      });

      const response = await apiClient.connectFacebook(
        accessToken,
        userInfo?.id,
        mappedPages
      );

      console.debug('[connectToBackend] Backend response:', response);
      setConnectionStatus('✅ Successfully connected to backend!');
      return response;
    } catch (error) {
      console.error('Backend connection error:', error);
      setConnectionStatus('❌ Backend connection failed: ' + (error.message || 'Unknown error'));
      throw error;
    }
  };

  // Icon Components

  const ImageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
      <circle cx="8.5" cy="8.5" r="1.5"></circle>
      <polyline points="21,15 16,10 5,21"></polyline>
    </svg>
  );

  const AutoReplyIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
    </svg>
  );

  const RocketIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M4.5 16.5c-1.5 1.26-2 5-2 5s3.74-.5 5-2c.71-.84.7-2.13-.09-2.91a2.18 2.18 0 0 0-2.91-.09z"></path>
      <path d="M12 15l-3-3a22 22 0 0 1 2-3.95A12.88 12.88 0 0 1 22 2c0 2.72-.78 7.5-6 11a22.35 22.35 0 0 1-4 2z"></path>
      <path d="M9 12H4s.55-3.03 2-4c1.62-1.08 5 0 5 0"></path>
      <path d="M12 15v5s3.03-.55 4-2c1.08-1.62 0-5 0-5"></path>
    </svg>
  );

  const AutoIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
    </svg>
  );

  const ManualIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M17 3a2.85 2.83 0 1 1 4 4L7.5 20.5 2 22l1.5-5.5L17 3z"/>
    </svg>
  );

  const VideoIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <polygon points="23 7 16 12 23 17 23 7"></polygon>
      <rect x="1" y="5" width="15" height="14" rx="2" ry="2"></rect>
    </svg>
  );

  const SparkleIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M9.937 15.5A2 2 0 0 0 8.5 14.063l-6.135-1.582a.5.5 0 0 1 0-.962L8.5 9.936A2 2 0 0 0 9.937 8.5l1.582-6.135a.5.5 0 0 1 .963 0L14.063 8.5A2 2 0 0 0 15.5 9.937l6.135 1.582a.5.5 0 0 1 0 .962L15.5 14.063a2 2 0 0 0-1.437 1.437l-1.582 6.135a.5.5 0 0 1-.963 0L9.937 15.5Z"/>
    </svg>
  );

  // Clean up any existing Facebook SDK instances
  const cleanupFacebookSDK = () => {
    const existingScript = document.getElementById('facebook-jssdk');
    if (existingScript) {
      existingScript.remove();
    }

    if (window.FB) {
      delete window.FB;
    }

    if (window.fbAsyncInit) {
      delete window.fbAsyncInit;
    }
  };

  // Facebook App Configuration - SINGLE APP SETUP
  const FACEBOOK_APPS = {
    LOGIN: process.env.REACT_APP_FACEBOOK_APP_ID || '1526961221410200',    // Using the page management app for everything
    PAGES: process.env.REACT_APP_FACEBOOK_APP_ID || '1526961221410200'     // Same app ID
  };

  const USE_DUAL_APPS = false; // Force single app mode

  // Load Facebook SDK function
  const loadFacebookSDK = (appId = FACEBOOK_APPS.LOGIN) => {
    return new Promise((resolve, reject) => {
      cleanupFacebookSDK();

      window.fbAsyncInit = function () {
        try {
          console.debug('[loadFacebookSDK] Initializing with App ID:', appId);

          window.FB.init({
            appId: appId,
            cookie: true,
            xfbml: true,
            version: 'v19.0'
          });

          // Store current app ID for reference
          window.CURRENT_FB_APP_ID = appId;
          resolve();
        } catch (error) {
          console.error('Facebook SDK initialization error:', error);
          reject(error);
        }
      };

      const script = document.createElement('script');
      script.id = 'facebook-jssdk';
      script.src = 'https://connect.facebook.net/en_US/sdk.js';
      script.async = true;
      script.defer = true;
      script.crossOrigin = 'anonymous';

      script.onerror = () => {
        reject(new Error('Failed to load Facebook SDK script'));
      };

      document.body.appendChild(script);
    });
  };

  const handleAutoInputChange = (e) => {
    const { name, value, files } = e.target;
    setAutoFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleManualInputChange = (e) => {
    const { name, value, files } = e.target;
    setManualFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handleMediaTypeChange = (type, formType) => {
    if (formType === 'auto') {
      setAutoFormData(prev => ({
        ...prev,
        mediaType: type,
        mediaFile: null
      }));
    } else {
      setManualFormData(prev => ({
        ...prev,
        mediaType: type,
        mediaFile: null
      }));
    }
  };

  // Generate content using Groq API
  const generatePostContent = async () => {
    if (!autoFormData.prompt.trim()) {
      setConnectionStatus('❌ Please enter a prompt for AI generation');
      return;
    }

    setAutoFormData(prev => ({ ...prev, isGenerating: true }));
    setConnectionStatus('🤖 Generating content with AI...');

    try {
      // Call your backend API to use Groq
      const response = await apiClient.generateContent(autoFormData.prompt);

      setAutoFormData(prev => ({
        ...prev,
        generatedContent: response.content,
        isGenerating: false
      }));

      setConnectionStatus('✅ Content generated successfully!');
    } catch (error) {
      console.error('Content generation error:', error);
      setConnectionStatus('❌ Failed to generate content: ' + (error.message || 'Unknown error'));
      setAutoFormData(prev => ({ ...prev, isGenerating: false }));
    }
  };

  // UPDATED: Replace Make.com auto-reply webhook with backend API
  const handleAutoReplyToggle = async () => {
    const newState = !autoReplyEnabled;
    setAutoReplyEnabled(newState);

    try {
      setConnectionStatus('🔄 Updating auto-reply setting...');

      await apiClient.toggleAutoReply(
        selectedPage?.id || 'user_profile',
        newState,
        'Thank you for your comment! We\'ll get back to you soon.'
      );

      setConnectionStatus(`${newState ? '✅' : '❌'} Auto-reply ${newState ? 'enabled' : 'disabled'}`);
    } catch (error) {
      console.error('Auto-reply toggle error:', error);
      setConnectionStatus('❌ Failed to update auto-reply setting');
      setAutoReplyEnabled(!newState); // Revert on error
    }
  };

  // Publish post to Facebook
  const publishPost = async () => {
    if (!selectedPage) {
      setConnectionStatus('❌ Please select a page first');
      return;
    }

    // Determine content based on active tab
    const isAutoMode = activeTab === 'auto';
    const content = isAutoMode ? autoFormData.generatedContent : manualFormData.message;
    const mediaType = isAutoMode ? autoFormData.mediaType : manualFormData.mediaType;
    const mediaFile = isAutoMode ? autoFormData.mediaFile : manualFormData.mediaFile;

    // Validation
    if (!content || content.trim() === '') {
      setConnectionStatus('❌ Please enter some content for your post');
      return;
    }

    if (mediaType !== 'none' && !mediaFile) {
      setConnectionStatus('❌ Please select a media file to upload');
      return;
    }

    try {
      setIsPublishing(true);
      setConnectionStatus('🔄 Publishing post to Facebook...');

      // If no media, just post text
      if (mediaType === 'none') {
        await postTextOnly(content);
      } else {
        await postWithMedia(content, mediaFile, mediaType);
      }

      setConnectionStatus('✅ Post published successfully!');

      // Clear form data after successful post
      if (isAutoMode) {
        setAutoFormData(prev => ({
          ...prev,
          prompt: '',
          generatedContent: '',
          mediaFile: null,
          mediaType: 'none'
        }));
      } else {
        setManualFormData(prev => ({
          ...prev,
          message: '',
          mediaFile: null,
          mediaType: 'none'
        }));
      }

    } catch (error) {
      console.error('Post publishing error:', error);
      setConnectionStatus('❌ Failed to publish post: ' + (error.message || 'Unknown error'));
    } finally {
      setIsPublishing(false);
    }
  };

  // Post text-only content
  const postTextOnly = async (message) => {
    return new Promise((resolve, reject) => {
      window.FB.api(`/${selectedPage.id}/feed`, 'POST', {
        message: message,
        access_token: selectedPage.access_token
      }, (response) => {
        console.debug('[FB.api] Post response:', response);
        if (response.error) {
          reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
        } else {
          resolve(response);
        }
      });
    });
  };

  // Post content with media (photo or video)
  const postWithMedia = async (message, file, mediaType) => {
    // Convert file to base64 for Facebook API
    const fileData = await fileToBase64(file);

    if (mediaType === 'photo') {
      return new Promise((resolve, reject) => {
        window.FB.api(`/${selectedPage.id}/photos`, 'POST', {
          caption: message,
          source: fileData,
          access_token: selectedPage.access_token
        }, (response) => {
          console.debug('[FB.api] Photo post response:', response);
          if (response.error) {
            reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
          } else {
            resolve(response);
          }
        });
      });
    } else if (mediaType === 'video') {
      return new Promise((resolve, reject) => {
        window.FB.api(`/${selectedPage.id}/videos`, 'POST', {
          description: message,
          source: fileData,
          access_token: selectedPage.access_token
        }, (response) => {
          console.debug('[FB.api] Video post response:', response);
          if (response.error) {
            reject(new Error(`${response.error.message} (Code: ${response.error.code})`));
          } else {
            resolve(response);
          }
        });
      });
    }
  };

  // Helper function to convert file to base64
  const fileToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  // Check URL for pages auth callback
  React.useEffect(() => {
    const urlParams = new URLSearchParams(window.location.hash.substring(1));
    const accessToken = urlParams.get('access_token');
    const state = urlParams.get('state');

    if (accessToken && state === 'pages_auth') {
      console.debug('[useEffect] Found pages auth callback in URL');
      // Send message to opener window (popup parent)
      if (window.opener) {
        window.opener.postMessage({
          type: 'FACEBOOK_PAGES_AUTH',
          accessToken: accessToken
        }, window.location.origin);
        window.close();
      } else {
        // Direct navigation case
        (async () => {
          setConnectionStatus('🔄 Processing pages authentication...');
          const { mappedPages, userInfo } = await fetchPages(accessToken);
          if (mappedPages.length > 0) {
            await connectToBackend(accessToken, userInfo, mappedPages);
          }
        })();
      }

      // Clean up URL
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, [fetchPages]);

  // Manual Pages App Login for Dual App Setup
  const loginWithPagesApp = async () => {
    setIsConnecting(true);
    setConnectionStatus('🔐 Connecting to Facebook Pages App...');

    try {
      // Direct redirect to pages app OAuth
      const pagesAuthUrl = `https://www.facebook.com/v19.0/dialog/oauth?` +
        `client_id=${FACEBOOK_APPS.PAGES}&` +
        `redirect_uri=${encodeURIComponent(window.location.origin)}&` +
        `scope=pages_show_list,pages_manage_posts,pages_manage_engagement,pages_read_engagement&` +
        `response_type=token&` +
        `state=pages_auth&` +
        `display=popup`;

      // Open in popup
      const popup = window.open(pagesAuthUrl, 'facebookPagesAuth', 'width=600,height=400');

      // Listen for popup message
      const handleMessage = (event) => {
        if (event.origin !== window.location.origin) return;

        if (event.data.type === 'FACEBOOK_PAGES_AUTH') {
          window.removeEventListener('message', handleMessage);
          popup.close();

          if (event.data.accessToken) {
            console.debug('[loginWithPagesApp] Got pages access token:', event.data.accessToken);
            // Use the pages access token to fetch pages
            (async () => {
              const { mappedPages, userInfo } = await fetchPages(event.data.accessToken);
              if (mappedPages.length > 0) {
                await connectToBackend(event.data.accessToken, userInfo, mappedPages);
              }
            })();
          } else {
            setConnectionStatus('❌ Failed to get pages app permissions');
            setIsConnecting(false);
          }
        }
      };

      window.addEventListener('message', handleMessage);

    } catch (error) {
      console.error('Pages app login error:', error);
      setConnectionStatus('❌ Pages app login failed: ' + error.message);
      setIsConnecting(false);
    }
  };

  const loginWithFacebook = async () => {
    // Check HTTPS requirement first
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      setConnectionStatus('❌ Facebook login requires HTTPS. Please use https://localhost:3001 or deploy with HTTPS');
      return;
    }

    // Check if user is still authenticated with our backend
    try {
      await apiClient.getCurrentUser();
    } catch (error) {
      setConnectionStatus('❌ Your session has expired. Please log out and log back in to connect Facebook.');
      setTimeout(() => {
        logout();
        navigate('/');
      }, 3000);
      return;
    }

    setIsConnecting(true);
    setConnectionStatus('📦 Loading Facebook SDK...');

    try {
      // For dual app setup, start with login app
      await loadFacebookSDK(FACEBOOK_APPS.LOGIN);

      if (!window.FB || typeof window.FB.login !== 'function') {
        setConnectionStatus('❌ Facebook SDK failed to load. Please refresh the page and try again.');
        setIsConnecting(false);
        return;
      }

      setConnectionStatus('🔐 Connecting to Facebook...');

      window.FB.login((response) => {
        console.debug('[FB.login] Response:', response);
        if (response.authResponse && response.authResponse.accessToken) {
          console.debug('[FB.login] accessToken:', response.authResponse.accessToken);
          setConnectionStatus('✅ Facebook login successful! Fetching pages...');

          // Use IIFE (Immediately Invoked Function Expression) to handle async operations
          (async () => {
            try {
              const { mappedPages, userInfo, needsPagesAuth } = await fetchPages(response.authResponse.accessToken);

              if (needsPagesAuth && USE_DUAL_APPS) {
                setConnectionStatus('✅ Basic login successful! Now connect your pages app for page management.');
                setFacebookConnected(true); // Mark as connected but show pages auth button
                return;
              }

              if (mappedPages.length > 0) {
                setConnectionStatus('🔄 Connecting to backend...');
                const backendResponse = await connectToBackend(response.authResponse.accessToken, userInfo, mappedPages);

                // Update frontend state with backend response if available
                if (backendResponse?.data?.pages) {
                  console.debug('[FB.login] Using pages from backend response:', backendResponse.data.pages);
                  setAvailablePages(mappedPages); // Keep original mapping for UI
                  setConnectionStatus(`✅ Connected successfully! ${backendResponse.data.pages_connected} pages synchronized with backend.`);
                }
              } else {
                setConnectionStatus('🔄 No pages found in Facebook API. Connecting to backend to check for existing connections...');

                try {
                  // Even if no pages found, try connecting to backend in case there are existing connections
                  await connectToBackend(response.authResponse.accessToken, userInfo, []);

                  // Check if backend has existing pages for this user
                  const existingAccounts = await apiClient.getSocialAccounts();
                  const facebookPages = existingAccounts.filter(acc => 
                    acc.platform === 'facebook' && acc.account_type === 'page' && acc.is_connected
                  );

                  if (facebookPages.length > 0) {
                    // Convert backend pages to frontend format
                    const backendMappedPages = facebookPages.map(page => ({
                      id: page.platform_user_id,
                      name: page.display_name,
                      category: page.platform_data?.category || 'Page',
                      access_token: page.access_token || response.authResponse.accessToken,
                      profilePicture: page.profile_picture_url || '',
                      canPost: page.platform_data?.can_post !== false,
                      canComment: page.platform_data?.can_comment !== false,
                      followerCount: page.follower_count || 0
                    }));

                    setAvailablePages(backendMappedPages);
                    setConnectionStatus(`✅ Found ${facebookPages.length} existing page(s) from previous connections!`);
                  } else {
                    setConnectionStatus('⚠️ No pages found. You may need to grant page permissions or create a Facebook page first.');
                  }
                } catch (backendError) {
                  console.error('[FB.login] Backend check failed:', backendError);
                  setConnectionStatus('⚠️ No pages found. You may need to grant page permissions or create a Facebook page first.');
                }
              }
            } catch (error) {
              console.error('[FB.login] Error in page fetching or backend connection:', error);
              setConnectionStatus('❌ Error during setup: ' + (error.message || 'Unknown error'));
              setIsConnecting(false);
              setFacebookConnected(false);
            }
          })();
        } else {
          if (response.status === 'not_authorized') {
            setConnectionStatus('❌ Please authorize the app to continue and select at least one page.');
          } else {
            setConnectionStatus('❌ Facebook login cancelled or failed');
          }
          setIsConnecting(false);
          console.warn('[FB.login] No accessToken found in response:', response);
        }
      }, {
        scope: [
          'public_profile',
          'pages_show_list',
          'pages_read_engagement', 
          'pages_manage_posts',
          'pages_manage_engagement',
          'pages_read_user_content',
          'pages_manage_metadata'
        ].join(','),
        enable_profile_selector: true,
        return_scopes: true,
        auth_type: 'rerequest',
        display: 'popup'  // Force popup display
      });
    } catch (error) {
      console.error('Facebook login error:', error);
      setConnectionStatus('❌ Facebook login failed: ' + error.message);
    }
  };

  // Always render something for the Facebook page
  return (
    <div className="facebook-page-container" style={{ padding: 32, maxWidth: 700, margin: '0 auto' }}>
      <h2>Facebook Page Automation</h2>
      {/* Show connection status */}
      {connectionStatus && (
        <div style={{ marginBottom: 16, color: connectionStatus.startsWith('✅') ? 'green' : connectionStatus.startsWith('❌') ? 'red' : '#333' }}>
          {connectionStatus}
        </div>
      )}

      {/* Show dual app mode indicator */}
      {USE_DUAL_APPS && (
        <div style={{ marginBottom: 16, padding: 12, background: '#e0f2fe', border: '1px solid #0891b2', borderRadius: 8, fontSize: 14 }}>
          🔄 <strong>Dual App Mode Active:</strong> Using separate apps for login ({FACEBOOK_APPS.LOGIN}) and page management ({FACEBOOK_APPS.PAGES})
        </div>
      )}
      {/* Show Connect button if not connected */}
      {!facebookConnected && (
        <div style={{ marginBottom: 24 }}>
          <button onClick={loginWithFacebook} disabled={isConnecting} style={{ padding: '10px 24px', fontSize: 18 }}>
            {isConnecting ? 'Connecting...' : 'Connect Facebook'}
          </button>
          <div style={{ marginTop: 10, color: '#666' }}>
            Connect your Facebook account to manage posts, auto-replies, and more!
          </div>
        </div>
      )}
      {/* Show re-request permissions button if no pages found after connection */}
      {facebookConnected && availablePages.length === 0 && !(USE_DUAL_APPS && (connectionStatus.includes('Now connect your pages app') || connectionStatus.includes('Pages app connection needed'))) && (
        <div style={{ marginTop: 24, color: '#b91c1c', background: '#fff7f7', padding: 16, borderRadius: 8 }}>
          <div style={{ marginBottom: 8 }}>
            <strong>No Facebook Pages found.</strong> <br />
            This may be because:
            <ul style={{ margin: '8px 0 0 20px', color: '#b91c1c' }}>
              <li>You did not select any pages during Facebook login.</li>
              <li>You denied required permissions.</li>
              <li>Your Facebook account does not have any managed pages.</li>
              <li><strong>Your Facebook app configuration is incorrect.</strong></li>
            </ul>
          </div>

          <div style={{ margin: '12px 0', padding: 12, background: '#fef3c7', color: '#92400e', borderRadius: 4, fontSize: 14 }}>
            <strong>🔧 Facebook App Setup Checklist:</strong>
            <ol style={{ margin: '8px 0 0 20px' }}>
              <li><strong>App Type:</strong> Should be "Consumer" or "Business" (not just page management)</li>
              <li><strong>Use Cases:</strong> Add both "Facebook Login" AND "Pages API"</li>
              <li><strong>App Review:</strong> Some permissions may need review for production</li>
              <li><strong>Development Mode:</strong> Make sure you're added as an app tester</li>
            </ol>
            <div style={{ marginTop: 8 }}>
              <strong>Login App ID:</strong> {FACEBOOK_APPS.LOGIN}<br/>
              <strong>Pages App ID:</strong> {FACEBOOK_APPS.PAGES}<br/>
              <strong>Using Dual Apps:</strong> {USE_DUAL_APPS ? 'Yes' : 'No'}<br/>
              <strong>Current Mode:</strong> {USE_DUAL_APPS ? 'Dual App Setup' : 'Single App Setup'}
            </div>
          </div>

          <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
            <button
              onClick={loginWithFacebook}
              style={{ padding: '8px 18px', background: '#1877f2', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 600 }}
            >
              Re-request Facebook Permissions
            </button>
            {USE_DUAL_APPS && (
              <button
                onClick={loginWithPagesApp}
                disabled={isConnecting}
                style={{ padding: '8px 18px', background: '#059669', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 600 }}
              >
                {isConnecting ? 'Connecting...' : 'Connect Pages App'}
              </button>
            )}
            <button
              onClick={() => {
                window.open('https://developers.facebook.com/apps/', '_blank');
              }}
              style={{ padding: '8px 18px', background: '#6b7280', color: '#fff', border: 'none', borderRadius: 4, fontWeight: 600 }}
            >
              Open Facebook App Settings
            </button>
          </div>
        </div>
      )}
      {/* Page selection UI if any pages exist */}
      {facebookConnected && availablePages.length > 0 && (
        <div className="page-select-section" style={{ marginTop: 24 }}>
          <label htmlFor="fb-page-select"><strong>Select a Facebook Page:</strong></label>
          <select
            id="fb-page-select"
            value={selectedPage ? selectedPage.id : ''}
            onChange={e => {
              const page = availablePages.find(p => p.id === e.target.value);
              if (page) {
                setSelectedPage(page);
                setConnectionStatus(`✅ Page "${page.name}" selected successfully! You can now manage posts and auto-replies.`);
                console.log('[Page Selection] Selected page:', page);
              } else {
                setSelectedPage(null);
                setConnectionStatus('⚠️ Please select a page to continue.');
              }
            }}
            style={{ marginLeft: 8, padding: '8px 12px', borderRadius: 4, border: '1px solid #ddd' }}
          >
            <option value="">-- Select Page --</option>
            {availablePages.map(page => (
              <option key={page.id} value={page.id}>
                {page.name} ({page.category}) {page.followerCount > 0 ? `- ${page.followerCount} followers` : ''}
              </option>
            ))}
          </select>
          {selectedPage && (
            <div style={{ marginTop: 12, padding: 12, background: '#f0f9ff', borderRadius: 8, border: '1px solid #0ea5e9' }}>
              <div style={{ display: 'flex', alignItems: 'center', marginBottom: 8 }}>
                {selectedPage.profilePicture && (
                  <img 
                    src={selectedPage.profilePicture} 
                    alt={selectedPage.name} 
                    width={40} 
                    height={40}
                    style={{ borderRadius: '50%', marginRight: 12 }} 
                  />
                )}
                <div>
                  <div style={{ fontWeight: 600, fontSize: 16 }}>{selectedPage.name}</div>
                  <div style={{ color: '#666', fontSize: 14 }}>{selectedPage.category}</div>
                </div>
              </div>
              <div style={{ fontSize: 14, color: '#0369a1' }}>
                <div>✅ Can post: {selectedPage.canPost ? 'Yes' : 'No'}</div>
                <div>✅ Can manage comments: {selectedPage.canComment ? 'Yes' : 'No'}</div>
                {selectedPage.followerCount > 0 && (
                  <div>👥 Followers: {selectedPage.followerCount.toLocaleString()}</div>
                )}
              </div>
            </div>
          )}
          {/* Show warning if pages exist but no selection */}
          {!selectedPage && (
            <div style={{ color: '#b91c1c', marginTop: 8, padding: 8, background: '#fef2f2', borderRadius: 4 }}>
              ⚠️ Please select a page from the dropdown above to enable posting and auto-reply features.
            </div>
          )}
          {/* Debug: show availablePages raw data */}
          <details style={{ marginTop: 12, fontSize: 13 }}>
            <summary>Debug: Show available pages data ({availablePages.length} found)</summary>
            <pre style={{ background: '#f3f3f3', padding: 8, borderRadius: 4, maxHeight: 200, overflow: 'auto' }}>{JSON.stringify(availablePages, null, 2)}</pre>
          </details>
        </div>
      )}

      {/* Show Pages App Login if basic login succeeded but pages auth needed */}
      {USE_DUAL_APPS && facebookConnected && availablePages.length === 0 && (connectionStatus.includes('Now connect your pages app') || connectionStatus.includes('Pages app connection needed')) && (
        <div style={{ marginTop: 24, padding: 16, background: '#f0fdf4', border: '1px solid #16a34a', borderRadius: 8 }}>
          <div style={{ marginBottom: 12 }}>
            <strong>✅ Basic Facebook login successful!</strong><br/>
            <span style={{ color: '#15803d' }}>Now connect your Pages app to manage Facebook pages:</span>
          </div>
          <button
            onClick={loginWithPagesApp}
            disabled={isConnecting}
            style={{ 
              padding: '12px 24px', 
              background: '#16a34a', 
              color: '#fff', 
              border: 'none', 
              borderRadius: 6, 
              fontWeight: 600,
              fontSize: 16
            }}
          >
            {isConnecting ? 'Connecting to Pages App...' : '🔗 Connect Facebook Pages App'}
          </button>
        </div>
      )}

      {/* Main functionality - only show when a page is selected */}
      {facebookConnected && selectedPage && (
        <div style={{ marginTop: 32 }}>
          {/* Auto-Reply Toggle */}
          <div style={{ marginBottom: 24, padding: 16, background: '#f8fafc', borderRadius: 8, border: '1px solid #e2e8f0' }}>
            <h3 style={{ margin: '0 0 12px 0', display: 'flex', alignItems: 'center', gap: 8 }}>
              <AutoReplyIcon />
              Auto-Reply to Comments
            </h3>
            <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
              <label style={{ display: 'flex', alignItems: 'center', gap: 8, cursor: 'pointer' }}>
                <input
                  type="checkbox"
                  checked={autoReplyEnabled}
                  onChange={handleAutoReplyToggle}
                  style={{ width: 18, height: 18 }}
                />
                {autoReplyEnabled ? 'Auto-reply is enabled' : 'Enable auto-reply to comments'}
              </label>
            </div>
            <p style={{ margin: '8px 0 0 0', fontSize: 14, color: '#64748b' }}>
              When enabled, the bot will automatically reply to new comments on your page posts.
            </p>
          </div>

          {/* Tab Navigation */}
          <div style={{ marginBottom: 24 }}>
            <div style={{ display: 'flex', borderBottom: '2px solid #e2e8f0' }}>
              <button
                onClick={() => setActiveTab('auto')}
                style={{
                  padding: '12px 24px',
                  border: 'none',
                  background: activeTab === 'auto' ? '#3b82f6' : 'transparent',
                  color: activeTab === 'auto' ? 'white' : '#64748b',
                  borderRadius: '8px 8px 0 0',
                  fontWeight: 600,
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 8
                }}
              >
                <AutoIcon />
                AI Auto Post
              </button>
              <button
                onClick={() => setActiveTab('manual')}
                style={{
                  padding: '12px 24px',
                  border: 'none',
                  background: activeTab === 'manual' ? '#3b82f6' : 'transparent',
                  color: activeTab === 'manual' ? 'white' : '#64748b',
                  borderRadius: '8px 8px 0 0',
                  fontWeight: 600,
                  cursor: 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 8
                }}
              >
                <ManualIcon />
                Manual Post
              </button>
            </div>
          </div>

          {/* Content based on active tab */}
          {activeTab === 'auto' && (
            <div>
              <h3 style={{ marginBottom: 16, display: 'flex', alignItems: 'center', gap: 8 }}>
                <SparkleIcon />
                AI-Generated Post
              </h3>
              <p style={{ color: '#64748b', marginBottom: 16 }}>
                Describe what you want to post about, and AI will generate engaging content for you.
              </p>

              <div style={{ marginBottom: 16 }}>
                <label style={{ display: 'block', marginBottom: 8, fontWeight: 600 }}>
                  Content Prompt:
                </label>
                <textarea
                  name="prompt"
                  value={autoFormData.prompt}
                  onChange={handleAutoInputChange}
                  placeholder="e.g., 'Create a motivational post about entrepreneurship' or 'Write about our latest product launch'"
                  style={{
                    width: '100%',
                    minHeight: 100,
                    padding: 12,
                    border: '1px solid #d1d5db',
                    borderRadius: 8,
                    fontSize: 14,
                    resize: 'vertical'
                  }}
                />
              </div>

              <button
                onClick={generatePostContent}
                disabled={autoFormData.isGenerating || !autoFormData.prompt.trim()}
                style={{
                  padding: '12px 24px',
                  background: autoFormData.isGenerating ? '#94a3b8' : '#10b981',
                  color: 'white',
                  border: 'none',
                  borderRadius: 8,
                  fontWeight: 600,
                  cursor: autoFormData.isGenerating ? 'not-allowed' : 'pointer',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 8,
                  marginBottom: 16
                }}
              >
                <SparkleIcon />
                {autoFormData.isGenerating ? 'Generating...' : 'Generate Content'}
              </button>

              {autoFormData.generatedContent && (
                <div style={{ marginBottom: 16 }}>
                  <label style={{ display: 'block', marginBottom: 8, fontWeight: 600 }}>
                    Generated Content:
                  </label>
                  <textarea
                    value={autoFormData.generatedContent}
                    onChange={(e) => setAutoFormData(prev => ({ ...prev, generatedContent: e.target.value }))}
                    style={{
                      width: '100%',
                      minHeight: 120,
                      padding: 12,
                      border: '1px solid #d1d5db',
                      borderRadius: 8,
                      fontSize: 14,
                      resize: 'vertical'
                    }}
                  />
                  <p style={{ fontSize: 12, color: '#64748b', marginTop: 4 }}>
                    You can edit the generated content before posting.
                  </p>
                </div>
              )}
            </div>
          )}

          {activeTab === 'manual' && (
            <div>
              <h3 style={{ marginBottom: 16, display: 'flex', alignItems: 'center', gap: 8 }}>
                <ManualIcon />
                Manual Post
              </h3>
              <p style={{ color: '#64748b', marginBottom: 16 }}>
                Create and publish your own custom post content.
              </p>

              <div style={{ marginBottom: 16 }}>
                <label style={{ display: 'block', marginBottom: 8, fontWeight: 600 }}>
                  Post Content:
                </label>
                <textarea
                  name="message"
                  value={manualFormData.message}
                  onChange={handleManualInputChange}
                  placeholder="Write your post content here..."
                  style={{
                    width: '100%',
                    minHeight: 120,
                    padding: 12,
                    border: '1px solid #d1d5db',
                    borderRadius: 8,
                    fontSize: 14,
                    resize: 'vertical'
                  }}
                />
              </div>
            </div>
          )}

          {/* Media Type Selection (for both tabs) */}
          <div style={{ marginBottom: 16 }}>
            <label style={{ display: 'block', marginBottom: 8, fontWeight: 600 }}>
              Media Type:
            </label>
            <div style={{ display: 'flex', gap: 12 }}>
              <button
                onClick={() => handleMediaTypeChange('none', activeTab)}
                style={{
                  padding: '8px 16px',
                  border: '1px solid #d1d5db',
                  background: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === 'none' ? '#3b82f6' : 'white',
                  color: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === 'none' ? 'white' : '#374151',
                  borderRadius: 6,
                  cursor: 'pointer',
                  fontWeight: 500
                }}
              >
                Text Only
              </button>
              <button
                onClick={() => handleMediaTypeChange('photo', activeTab)}
                style={{
                  padding: '8px 16px',
                  border: '1px solid #d1d5db',
                  background: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === 'photo' ? '#3b82f6' : 'white',
                  color: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === 'photo' ? 'white' : '#374151',
                  borderRadius: 6,
                  cursor: 'pointer',
                  fontWeight: 500,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 4
                }}
              >
                <ImageIcon />
                Photo
              </button>
              <button
                onClick={() => handleMediaTypeChange('video', activeTab)}
                style={{
                  padding: '8px 16px',
                  border: '1px solid #d1d5db',
                  background: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === 'video' ? '#3b82f6' : 'white',
                  color: (activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType) === 'video' ? 'white' : '#374151',
                  borderRadius: 6,
                  cursor: 'pointer',
                  fontWeight: 500,
                  display: 'flex',
                  alignItems: 'center',
                  gap: 4
                }}
              >
                <VideoIcon />
                Video
              </button>
            </div>
          </div>

          {/* Media File Upload */}
          {((activeTab === 'auto' && autoFormData.mediaType !== 'none') || 
            (activeTab === 'manual' && manualFormData.mediaType !== 'none')) && (
            <div style={{ marginBottom: 16 }}>
              <label style={{ display: 'block', marginBottom: 8, fontWeight: 600 }}>
                Upload {activeTab === 'auto' ? autoFormData.mediaType : manualFormData.mediaType}:
              </label>
              <input
                type="file"
                name="mediaFile"
                accept={activeTab === 'auto' 
                  ? (autoFormData.mediaType === 'photo' ? 'image/*' : 'video/*')
                  : (manualFormData.mediaType === 'photo' ? 'image/*' : 'video/*')
                }
                onChange={activeTab === 'auto' ? handleAutoInputChange : handleManualInputChange}
                style={{
                  width: '100%',
                  padding: 8,
                  border: '1px solid #d1d5db',
                  borderRadius: 6
                }}
              />
            </div>
          )}

          {/* Post Button */}
          <div style={{ marginTop: 24 }}>
            <button
              style={{
                padding: '12px 32px',
                background: isPublishing ? '#94a3b8' : '#1877f2',
                color: 'white',
                border: 'none',
                borderRadius: 8,
                fontWeight: 600,
                cursor: isPublishing ? 'not-allowed' : 'pointer',
                fontSize: 16,
                display: 'flex',
                alignItems: 'center',
                gap: 8,
                opacity: (isPublishing || !selectedPage || 
                         (activeTab === 'auto' && !autoFormData.generatedContent.trim()) ||
                         (activeTab === 'manual' && !manualFormData.message.trim())) ? 0.6 : 1
              }}
              onClick={publishPost}
              disabled={isPublishing || !selectedPage || 
                       (activeTab === 'auto' && !autoFormData.generatedContent.trim()) ||
                       (activeTab === 'manual' && !manualFormData.message.trim())}
            >
              <RocketIcon />
              {isPublishing ? 'Publishing...' : 'Publish Post'}
            </button>
            {/* Helper text */}
            <p style={{ fontSize: 14, color: '#64748b', marginTop: 8 }}>
              {!selectedPage ? 'Please select a page first' :
               activeTab === 'auto' && !autoFormData.generatedContent.trim() ? 'Generate content first' :
               activeTab === 'manual' && !manualFormData.message.trim() ? 'Enter your message first' :
               'Ready to publish to your Facebook page'}
            </p>
          </div>
        </div>
      )}
    </div>
  );
}


export default FacebookPage;
