import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import apiClient from '../services/apiClient';
import './SocialMediaPage.css';

function SocialMediaPageWithBackend() {
  const navigate = useNavigate();
  const { user, isAuthenticated, logout } = useAuth();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');
  const [hoveredCard, setHoveredCard] = useState(null);
  
  // Facebook OAuth states
  const [pageAccessToken, setPageAccessToken] = useState('');
  const [pages, setPages] = useState([]);
  const [selectedPage, setSelectedPage] = useState(null);
  const [facebookConnected, setFacebookConnected] = useState(false);
  
  // Auto-reply toggle state
  const [autoReplyEnabled, setAutoReplyEnabled] = useState(false);
  
  // Form data state
  const [formData, setFormData] = useState({
    message: '',
    image: null
  });
  
  // Post type selection state
  const [postType, setPostType] = useState('post-auto');

  // Icon Components (same as before)
  const PostTypeIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
      <polyline points="14,2 14,8 20,8"></polyline>
    </svg>
  );

  const MessageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"></path>
    </svg>
  );

  const AutoReplyIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
    </svg>
  );

  const LogoutIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
      <polyline points="16,17 21,12 16,7"></polyline>
      <line x1="21" y1="12" x2="9" y2="12"></line>
    </svg>
  );

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handlePostTypeChange = (e) => {
    setPostType(e.target.value);
    if (!e.target.value.includes('image')) {
      setFormData(prev => ({ ...prev, image: null }));
    }
  };

  // NEW: Replace Make.com auto-reply webhook with backend API
  const handleAutoReplyToggle = async () => {
    const newState = !autoReplyEnabled;
    setAutoReplyEnabled(newState);
    
    try {
      setConnectionStatus('🔄 Updating auto-reply setting...');
      
      await apiClient.toggleAutoReply(
        selectedPage?.id || 'user_profile',
        newState,
        'Thank you for your comment! We\'ll get back to you soon.'
      );

      setConnectionStatus(`${newState ? '✅' : '❌'} Auto-reply ${newState ? 'enabled' : 'disabled'}`);
    } catch (error) {
      console.error('Auto-reply toggle error:', error);
      setConnectionStatus('❌ Failed to update auto-reply setting');
      setAutoReplyEnabled(!newState); // Revert on error
    }
  };

  // Facebook SDK loading (same as before)
  const loadFacebookSDK = () => {
    return new Promise((resolve, reject) => {
      if (window.FB) {
        resolve();
        return;
      }
      
      window.fbAsyncInit = function () {
        try {
          const appId = process.env.REACT_APP_FACEBOOK_APP_ID || '1526961221410200';
          
          window.FB.init({
            appId: appId,
            cookie: true,
            xfbml: true,
            version: 'v23.0'
          });

          resolve();
        } catch (error) {
          console.error('Facebook SDK initialization error:', error);
          reject(error);
        }
      };

      const script = document.createElement('script');
      script.src = 'https://connect.facebook.net/en_US/sdk.js';
      script.async = true;
      script.defer = true;
      script.crossOrigin = 'anonymous';
      script.onerror = () => reject(new Error('Failed to load Facebook SDK'));
      document.body.appendChild(script);
    });
  };

  const loginWithFacebook = async () => {
    setIsConnecting(true);
    setConnectionStatus('📦 Loading Facebook SDK...');

    try {
      await loadFacebookSDK();
      setConnectionStatus('🔐 Connecting to Facebook...');
      
      window.FB.login(async (response) => {
        if (response.authResponse) {
          setConnectionStatus('✅ Facebook login successful!');
          await connectToBackend(response.authResponse);
        } else {
          setConnectionStatus('❌ Facebook login cancelled or failed');
          setIsConnecting(false);
        }
      }, {
        scope: 'public_profile,pages_show_list,pages_read_engagement,pages_manage_posts'
      });
    } catch (error) {
      console.error('Facebook login error:', error);
      setConnectionStatus('❌ Facebook login failed: ' + error.message);
      setIsConnecting(false);
    }
  };

  // NEW: Connect Facebook to backend instead of Make.com
  const connectToBackend = async (authResponse) => {
    try {
      setConnectionStatus('🔗 Connecting to backend...');
      
      // Get user info from Facebook
      window.FB.api('/me', { fields: 'id,name,email' }, async (userInfo) => {
        if (userInfo.error) {
          throw new Error(userInfo.error.message);
        }

        // Get user's pages
        window.FB.api('/me/accounts', { fields: 'id,name,category,access_token' }, async (pagesResponse) => {
          const pages = pagesResponse.data || [];
          
          // Connect to our backend
          const result = await apiClient.connectFacebook(
            authResponse.accessToken,
            userInfo.id,
            pages.map(page => ({
              id: page.id,
              name: page.name,
              category: page.category || 'Page',
              access_token: page.access_token,
              can_post: true
            }))
          );

          setFacebookConnected(true);
          setPageAccessToken(authResponse.accessToken);
          setSelectedPage({
            id: userInfo.id,
            name: userInfo.name,
            access_token: authResponse.accessToken,
            category: 'Personal Profile',
            isUserProfile: true
          });
          
          setConnectionStatus(`✅ Connected successfully! ${result.data?.pages_connected || 0} pages connected.`);
          setIsConnecting(false);
        });
      });
    } catch (error) {
      console.error('Backend connection error:', error);
      setConnectionStatus('❌ Failed to connect to backend: ' + (error.message || 'Unknown error'));
      setIsConnecting(false);
      setFacebookConnected(false);
    }
  };

  // NEW: Replace Make.com post webhook with backend API
  const handleFacebookConnect = async () => {
    if (!facebookConnected) {
      loginWithFacebook();
      return;
    }

    // Validate required fields
    if (!formData.message || formData.message.trim() === '') {
      setConnectionStatus('❌ Please enter a message');
      return;
    }

    try {
      setIsConnecting(true);
      setConnectionStatus('🚀 Posting to Facebook...');
      
      // Use our backend API instead of Make.com webhook
      // For now, we'll just indicate if there's an image (file upload handling can be added later)
      const result = await apiClient.createFacebookPost(
        selectedPage?.id || 'user_profile',
        formData.message,
        postType,
        formData.image ? formData.image.name : null
      );

      setConnectionStatus(`✅ ${result.message}`);
      setFormData({ message: '', image: null }); // Clear form
      setIsConnecting(false);
    } catch (error) {
      console.error('Post creation error:', error);
      setConnectionStatus('❌ Failed to create post: ' + error.message);
      setIsConnecting(false);
    }
  };

  const handleLogout = async () => {
    await logout();
    setFacebookConnected(false);
    setSelectedPage(null);
    setPageAccessToken('');
    setConnectionStatus('');
    navigate('/');
  };

  // Show login form if not authenticated
  if (!isAuthenticated) {
    return (
      <div className="social-media-page">
        <div className="container">
          <h1>Please login to continue</h1>
          <button onClick={() => navigate('/')} className="btn primary">
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="social-media-page">
      <div className="container">
        {/* Header */}
        <header className="page-header">
          <div className="header-content">
            <h1>Social Media Automation</h1>
            <div className="user-info">
              <span>Welcome, {user?.full_name || user?.username}</span>
              <button onClick={handleLogout} className="logout-btn">
                <LogoutIcon />
                Logout
              </button>
            </div>
          </div>
        </header>

        {/* Facebook Section */}
        <div className="platform-section">
          <div className="platform-header">
            <div className="platform-info">
              <div className="platform-icon facebook">📘</div>
              <div>
                <h3>Facebook</h3>
                <span className={`status ${facebookConnected ? 'connected' : 'disconnected'}`}>
                  {facebookConnected ? 'Connected' : 'Not Connected'}
                </span>
              </div>
            </div>
          </div>

          {/* Facebook Connection */}
          {!facebookConnected ? (
            <div className="connection-area">
              <button
                onClick={loginWithFacebook}
                disabled={isConnecting}
                className="connect-btn facebook"
              >
                {isConnecting ? 'Connecting...' : 'Connect to Facebook'}
              </button>
            </div>
          ) : (
            <div className="automation-controls">
              {/* Post Type Selection */}
              <div className="control-group">
                <label>
                  <PostTypeIcon />
                  Post Type
                </label>
                <select value={postType} onChange={handlePostTypeChange}>
                  <option value="post-auto">Auto Post</option>
                  <option value="post-manual">Manual Post</option>
                </select>
              </div>

              {/* Message Input */}
              <div className="control-group">
                <label>
                  <MessageIcon />
                  Message
                </label>
                <textarea
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  placeholder="What's on your mind?"
                  rows={4}
                />
              </div>

              {/* Image Upload */}
              <div className="control-group">
                <label>Image (Optional)</label>
                <input
                  type="file"
                  name="image"
                  onChange={handleInputChange}
                  accept="image/*"
                />
              </div>

              {/* Auto Reply Toggle */}
              <div className="control-group">
                <label>
                  <AutoReplyIcon />
                  Auto-Reply to Comments
                </label>
                <div className="toggle-container">
                  <input
                    type="checkbox"
                    checked={autoReplyEnabled}
                    onChange={handleAutoReplyToggle}
                    id="auto-reply-toggle"
                  />
                  <label htmlFor="auto-reply-toggle" className="toggle-label">
                    {autoReplyEnabled ? 'Enabled' : 'Disabled'}
                  </label>
                </div>
              </div>

              {/* Post Button */}
              <button
                onClick={handleFacebookConnect}
                disabled={isConnecting || !formData.message.trim()}
                className="post-btn"
              >
                {isConnecting ? 'Posting...' : 'Create Post'}
              </button>
            </div>
          )}

          {/* Status Display */}
          {connectionStatus && (
            <div className="status-message">
              {connectionStatus}
            </div>
          )}
        </div>

        {/* Backend Status */}
        <div className="backend-status">
          <h4>✅ Using FastAPI Backend (Replacing Make.com)</h4>
          <p>All automation is now handled by your own backend server.</p>
        </div>
      </div>
    </div>
  );
}

export default SocialMediaPageWithBackend;
