import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function SocialMediaPage() {
  const navigate = useNavigate();
  const [selectedPlatform, setSelectedPlatform] = useState(null);

  const socialPlatforms = [
    {
      id: 'instagram',
      name: 'Instagram',
      description: 'Automate posts, replies, and direct messages on Instagram',
      color: '#E4405F',
      gradient: 'linear-gradient(135deg, #f09433 0%, #e6683c 25%, #dc2743 50%, #cc2366 75%, #bc1888 100%)',
      icon: (
        <svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor">
          <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
        </svg>
      ),
      features: ['Auto-Posting', 'Auto reply comment', 'Response to DM'],
      status: 'Not Connected',
      stats: { posts: 0, replies: 0, messages: 0 }
    },
    {
      id: 'facebook',
      name: 'Facebook',
      description: 'Automate posts, replies, and messages on Facebook',
      color: '#1877F2',
      gradient: 'linear-gradient(135deg, #1877F2 0%, #42a5f5 100%)',
      icon: (
        <svg width="48" height="48" viewBox="0 0 24 24" fill="currentColor">
          <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
        </svg>
      ),
      features: ['Auto-Posting', 'Auto reply comment', 'Response to DM'],
      status: 'Not Connected',
      stats: { posts: 0, replies: 0, messages: 0 }
    }
  ];

  return (
    <div className="page-container">
      <header className="page-header">
        <button onClick={() => navigate('/')} className="back-btn">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
          Back to Dashboard
        </button>
        <div className="page-title-section">
          <h1 className="page-title">Social Media Automation</h1>
          <p className="page-subtitle">Connect and automate your social media platforms with advanced AI-powered features</p>
        </div>
      </header>

      <main className="page-main">
        <div className="platforms-container">
          <div className="platforms-grid">
            {socialPlatforms.map((platform) => (
              <div
                key={platform.id}
                className={`platform-card ${selectedPlatform === platform.id ? 'selected' : ''}`}
                onClick={() => setSelectedPlatform(selectedPlatform === platform.id ? null : platform.id)}
                style={{'--platform-color': platform.color, '--platform-gradient': platform.gradient}}
              >
                <div className="platform-status">
                  <span className={`status-badge ${platform.status.toLowerCase().replace(' ', '-')}`}>
                    {platform.status}
                  </span>
                </div>

                <div className="platform-header">
                  <div className="platform-icon-wrapper">
                    <div className="platform-icon" style={{color: platform.color}}>
                      {platform.icon}
                    </div>
                    <div className="icon-background"></div>
                  </div>
                  <div className="platform-info">
                    <h3 className="platform-name">{platform.name}</h3>
                    <p className="platform-description">{platform.description}</p>
                  </div>
                </div>

                <div className="platform-stats">
                  <div className="stat-item">
                    <span className="stat-number">{platform.stats.posts}</span>
                    <span className="stat-label">Posts</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-number">{platform.stats.replies}</span>
                    <span className="stat-label">Replies</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-number">{platform.stats.messages}</span>
                    <span className="stat-label">Messages</span>
                  </div>
                </div>

                <div className="platform-features">
                  <h4>Automation Features:</h4>
                  <div className="features-list">
                    {platform.features.map((feature, index) => (
                      <div key={index} className="feature-item">
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <polyline points="20,6 9,17 4,12"></polyline>
                        </svg>
                        <span>{feature}</span>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="platform-actions">
                  <button className="connect-btn">
                    <span>Connect {platform.name}</span>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M7 17l9.2-9.2M17 17V7H7"/>
                    </svg>
                  </button>
                  <button className="configure-btn" disabled={platform.status === 'Not Connected'}>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <circle cx="12" cy="12" r="3"></circle>
                      <path d="M12 1v6m0 6v6m11-7h-6m-6 0H1m15.5-3.5L17 10l-1.5-1.5M8.5 15.5L7 14l-1.5 1.5m0-11L7 10l1.5 1.5m7-7L14 7l1.5 1.5"></path>
                    </svg>
                    Configure
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {selectedPlatform && (
          <div className="platform-details">
            <div className="details-content">
              <div className="details-header">
                <h3>Configure {socialPlatforms.find(p => p.id === selectedPlatform)?.name} Automation</h3>
                <button 
                  className="close-details" 
                  onClick={() => setSelectedPlatform(null)}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <line x1="18" y1="6" x2="6" y2="18"></line>
                    <line x1="6" y1="6" x2="18" y2="18"></line>
                  </svg>
                </button>
              </div>
              
              <div className="config-form">
                <div className="form-section">
                  <h4>Auto-Posting Settings</h4>
                  <div className="form-group">
                    <label>Posting Schedule</label>
                    <select>
                      <option>Daily at 9:00 AM</option>
                      <option>3 times per week</option>
                      <option>Weekdays only</option>
                      <option>Custom schedule</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Content Type</label>
                    <div className="checkbox-group">
                      <label><input type="checkbox" defaultChecked /> Images</label>
                      <label><input type="checkbox" defaultChecked /> Videos</label>
                      <label><input type="checkbox" /> Stories</label>
                      <label><input type="checkbox" /> Reels</label>
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h4>Auto Reply Settings</h4>
                  <div className="form-group">
                    <label>Reply Speed</label>
                    <select>
                      <option>Instant (0-1 min)</option>
                      <option>Quick (1-5 min)</option>
                      <option>Normal (5-15 min)</option>
                      <option>Delayed (15-60 min)</option>
                    </select>
                  </div>
                  <div className="form-group">
                    <label>Reply Types</label>
                    <div className="checkbox-group">
                      <label><input type="checkbox" defaultChecked /> Thank you messages</label>
                      <label><input type="checkbox" defaultChecked /> FAQ responses</label>
                      <label><input type="checkbox" /> Custom keywords</label>
                    </div>
                  </div>
                </div>

                <div className="form-section">
                  <h4>DM Response Settings</h4>
                  <div className="form-group">
                    <label>Auto-reply to DMs</label>
                    <div className="toggle-group">
                      <label className="toggle">
                        <input type="checkbox" />
                        <span className="toggle-slider"></span>
                      </label>
                    </div>
                  </div>
                  <div className="form-group">
                    <label>Welcome Message</label>
                    <textarea placeholder="Hi! Thanks for reaching out. I'll get back to you soon!" rows="3"></textarea>
                  </div>
                </div>

                <div className="form-actions">
                  <button className="save-config-btn">
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <polyline points="20,6 9,17 4,12"></polyline>
                    </svg>
                    Save Configuration
                  </button>
                  <button className="test-config-btn">
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <circle cx="12" cy="12" r="10"></circle>
                      <polygon points="10,8 16,12 10,16 10,8"></polygon>
                    </svg>
                    Test Settings
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      <style jsx>{`
        .platforms-container {
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 20px;
        }

        .platforms-grid {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 30px;
          margin-bottom: 30px;
        }

        .platform-card {
          background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255,255,255,0.1);
          border-radius: 20px;
          padding: 30px;
          cursor: pointer;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          position: relative;
          overflow: hidden;
        }

        .platform-card::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          height: 4px;
          background: var(--platform-gradient);
          opacity: 0;
          transition: opacity 0.3s ease;
        }

        .platform-card:hover::before {
          opacity: 1;
        }

        .platform-card:hover {
          transform: translateY(-8px);
          box-shadow: 0 20px 40px rgba(0,0,0,0.1);
          border-color: rgba(255,255,255,0.2);
        }

        .platform-card.selected {
          border-color: var(--platform-color);
          box-shadow: 0 0 30px rgba(var(--platform-color), 0.3);
        }

        .platform-card.selected::before {
          opacity: 1;
        }

        .platform-status {
          position: absolute;
          top: 20px;
          right: 20px;
        }

        .status-badge {
          padding: 6px 12px;
          border-radius: 20px;
          font-size: 12px;
          font-weight: 600;
          text-transform: uppercase;
          letter-spacing: 0.5px;
        }

        .status-badge.not-connected {
          background: rgba(239, 68, 68, 0.1);
          color: #ef4444;
          border: 1px solid rgba(239, 68, 68, 0.2);
        }

        .status-badge.connected {
          background: rgba(34, 197, 94, 0.1);
          color: #22c55e;
          border: 1px solid rgba(34, 197, 94, 0.2);
        }

        .platform-header {
          display: flex;
          align-items: flex-start;
          gap: 20px;
          margin-bottom: 25px;
        }

        .platform-icon-wrapper {
          position: relative;
        }

        .platform-icon {
          position: relative;
          z-index: 2;
        }

        .icon-background {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          width: 80px;
          height: 80px;
          background: var(--platform-gradient);
          border-radius: 50%;
          opacity: 0.1;
          z-index: 1;
        }

        .platform-info {
          flex: 1;
        }

        .platform-name {
          font-size: 24px;
          font-weight: 700;
          margin: 0 0 8px 0;
          color: #fff;
        }

        .platform-description {
          font-size: 14px;
          color: rgba(255,255,255,0.7);
          margin: 0;
          line-height: 1.5;
        }

        .platform-stats {
          display: flex;
          gap: 20px;
          margin-bottom: 25px;
          padding: 20px;
          background: rgba(255,255,255,0.05);
          border-radius: 12px;
          border: 1px solid rgba(255,255,255,0.1);
        }

        .stat-item {
          text-align: center;
          flex: 1;
        }

        .stat-number {
          display: block;
          font-size: 24px;
          font-weight: 700;
          color: var(--platform-color);
          margin-bottom: 4px;
        }

        .stat-label {
          font-size: 12px;
          color: rgba(255,255,255,0.6);
          text-transform: uppercase;
          letter-spacing: 0.5px;
        }

        .platform-features h4 {
          font-size: 16px;
          font-weight: 600;
          margin: 0 0 15px 0;
          color: #fff;
        }

        .features-list {
          display: flex;
          flex-direction: column;
          gap: 12px;
        }

        .feature-item {
          display: flex;
          align-items: center;
          gap: 10px;
          font-size: 14px;
          color: rgba(255,255,255,0.8);
        }

        .feature-item svg {
          color: var(--platform-color);
          flex-shrink: 0;
        }

        .platform-actions {
          display: flex;
          gap: 12px;
          margin-top: 25px;
        }

        .connect-btn, .configure-btn {
          flex: 1;
          padding: 12px 20px;
          border: none;
          border-radius: 12px;
          font-size: 14px;
          font-weight: 600;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          transition: all 0.3s ease;
        }

        .connect-btn {
          background: var(--platform-gradient);
          color: white;
        }

        .connect-btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(0,0,0,0.2);
        }

        .configure-btn {
          background: rgba(255,255,255,0.1);
          color: rgba(255,255,255,0.8);
          border: 1px solid rgba(255,255,255,0.2);
        }

        .configure-btn:hover:not(:disabled) {
          background: rgba(255,255,255,0.2);
          color: white;
        }

        .configure-btn:disabled {
          opacity: 0.5;
          cursor: not-allowed;
        }

        .platform-details {
          background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255,255,255,0.1);
          border-radius: 20px;
          padding: 0;
          overflow: hidden;
          animation: slideIn 0.3s ease-out;
        }

        @keyframes slideIn {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .details-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding: 30px 30px 0 30px;
          margin-bottom: 20px;
        }

        .details-header h3 {
          font-size: 24px;
          font-weight: 700;
          color: #fff;
          margin: 0;
        }

        .close-details {
          background: rgba(255,255,255,0.1);
          border: none;
          border-radius: 8px;
          width: 40px;
          height: 40px;
          display: flex;
          align-items: center;
          justify-content: center;
          cursor: pointer;
          color: rgba(255,255,255,0.7);
          transition: all 0.3s ease;
        }

        .close-details:hover {
          background: rgba(255,255,255,0.2);
          color: white;
        }

        .config-form {
          padding: 0 30px 30px 30px;
        }

        .form-section {
          margin-bottom: 30px;
          padding: 25px;
          background: rgba(255,255,255,0.05);
          border-radius: 16px;
          border: 1px solid rgba(255,255,255,0.1);
        }

        .form-section h4 {
          font-size: 18px;
          font-weight: 600;
          color: #fff;
          margin: 0 0 20px 0;
        }

        .form-group {
          margin-bottom: 20px;
        }

        .form-group:last-child {
          margin-bottom: 0;
        }

        .form-group label {
          display: block;
          font-size: 14px;
          font-weight: 500;
          color: rgba(255,255,255,0.9);
          margin-bottom: 8px;
        }

        .form-group select, .form-group textarea {
          width: 100%;
          padding: 12px 16px;
          border: 1px solid rgba(255,255,255,0.2);
          border-radius: 8px;
          background: rgba(255,255,255,0.1);
          color: #fff;
          font-size: 14px;
          transition: all 0.3s ease;
        }

        .form-group select:focus, .form-group textarea:focus {
          outline: none;
          border-color: var(--platform-color);
          box-shadow: 0 0 0 3px rgba(var(--platform-color), 0.1);
        }

        .checkbox-group {
          display: flex;
          flex-wrap: wrap;
          gap: 15px;
        }

        .checkbox-group label {
          display: flex;
          align-items: center;
          gap: 8px;
          font-size: 14px;
          color: rgba(255,255,255,0.8);
          cursor: pointer;
          margin-bottom: 0;
        }

        .toggle-group {
          display: flex;
          align-items: center;
        }

        .toggle {
          position: relative;
          display: inline-block;
          width: 50px;
          height: 24px;
        }

        .toggle input {
          opacity: 0;
          width: 0;
          height: 0;
        }

        .toggle-slider {
          position: absolute;
          cursor: pointer;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background-color: rgba(255,255,255,0.2);
          transition: 0.3s;
          border-radius: 24px;
        }

        .toggle-slider:before {
          position: absolute;
          content: "";
          height: 18px;
          width: 18px;
          left: 3px;
          bottom: 3px;
          background-color: white;
          transition: 0.3s;
          border-radius: 50%;
        }

        .toggle input:checked + .toggle-slider {
          background-color: var(--platform-color);
        }

        .toggle input:checked + .toggle-slider:before {
          transform: translateX(26px);
        }

        .form-actions {
          display: flex;
          gap: 15px;
          margin-top: 30px;
        }

        .save-config-btn, .test-config-btn {
          flex: 1;
          padding: 14px 24px;
          border: none;
          border-radius: 12px;
          font-size: 14px;
          font-weight: 600;
          cursor: pointer;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 8px;
          transition: all 0.3s ease;
        }

        .save-config-btn {
          background: linear-gradient(135deg, #22c55e 0%, #16a34a 100%);
          color: white;
        }

        .save-config-btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(34, 197, 94, 0.3);
        }

        .test-config-btn {
          background: rgba(255,255,255,0.1);
          color: rgba(255,255,255,0.9);
          border: 1px solid rgba(255,255,255,0.2);
        }

        .test-config-btn:hover {
          background: rgba(255,255,255,0.2);
          color: white;
          transform: translateY(-2px);
        }

        @media (max-width: 768px) {
          .platforms-grid {
            grid-template-columns: 1fr;
            gap: 20px;
          }
          
          .platform-card {
            padding: 20px;
          }
          
          .form-actions {
            flex-direction: column;
          }
        }
      `}</style>
    </div>
  );
}

export default SocialMediaPage;
