import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import apiClient from '../services/apiClient';
import { useAuth } from '../contexts/AuthContext';
import './InstagramPage.css';

const InstagramPage = () => {
  const navigate = useNavigate();
  const { isAuthenticated, loading: authLoading, user } = useAuth();
  const [isConnected, setIsConnected] = useState(false);
  const [instagramAccounts, setInstagramAccounts] = useState([]);
  const [selectedAccount, setSelectedAccount] = useState('');
  const [postContent, setPostContent] = useState('');
  const [postImage, setPostImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [sdkLoaded, setSdkLoaded] = useState(false);
  const [fbAccessToken, setFbAccessToken] = useState(null);
  const [generatingAI, setGeneratingAI] = useState(false);
  const [userMedia, setUserMedia] = useState([]);
  const [loadingMedia, setLoadingMedia] = useState(false);
  const [activeTab, setActiveTab] = useState('connect');
  const [aiPrompt, setAiPrompt] = useState('');
  const [showAI, setShowAI] = useState(false);

  // Instagram App ID (different from Facebook App ID)
  const INSTAGRAM_APP_ID = process.env.REACT_APP_INSTAGRAM_APP_ID || '697225659875731';

  // Check authentication on mount
  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      navigate('/login');
    }
  }, [isAuthenticated, authLoading, navigate]);

  // Load Facebook SDK
  useEffect(() => {
    const loadFacebookSDK = () => {
      if (window.FB) {
        setSdkLoaded(true);
        return;
      }

      window.fbAsyncInit = function() {
        window.FB.init({
          appId: INSTAGRAM_APP_ID,
          cookie: true,
          xfbml: true,
          version: 'v18.0'
        });
        setSdkLoaded(true);
      };

      (function(d, s, id) {
        var js, fjs = d.getElementsByTagName(s)[0];
        if (d.getElementById(id)) return;
        js = d.createElement(s); js.id = id;
        js.src = "https://connect.facebook.net/en_US/sdk.js";
        fjs.parentNode.insertBefore(js, fjs);
      }(document, 'script', 'facebook-jssdk'));
    };

    if (isAuthenticated) {
      loadFacebookSDK();
    }
  }, [isAuthenticated, INSTAGRAM_APP_ID]);

  const handleFacebookLogin = () => {
    if (!window.FB) {
      setMessage('Facebook SDK not loaded. Please refresh the page.');
      return;
    }

    setMessage('Connecting to Facebook...');
    
    window.FB.login((response) => {
      if (response.authResponse) {
        setFbAccessToken(response.authResponse.accessToken);
        fetchInstagramAccounts(response.authResponse.accessToken);
      } else {
        setMessage('Facebook login failed or was cancelled.');
      }
    }, {
      scope: 'pages_show_list,instagram_basic,pages_read_engagement,business_management'
    });
  };

  const fetchInstagramAccounts = async (accessToken) => {
    try {
      setMessage('Fetching Instagram accounts...');
      
      const response = await apiClient.connectInstagram(accessToken);
      
      if (response.success && response.data.accounts) {
        setInstagramAccounts(response.data.accounts);
        setIsConnected(true);
        setActiveTab('accounts');
        setMessage(`Connected! Found ${response.data.accounts.length} Instagram account(s).`);
      } else {
        setMessage(response.error || 'No Instagram Business accounts found.');
      }
    } catch (error) {
      console.error('Error fetching Instagram accounts:', error);
      setMessage(`Error: ${error.response?.data?.detail || error.message}`);
    }
  };

  const handleCreatePost = async () => {
    if (!selectedAccount || (!postContent.trim() && !postImage)) {
      setMessage('Please select an account and enter content or select an image');
      return;
    }

    setLoading(true);
    setMessage('Creating Instagram post...');

    try {
      const formData = new FormData();
      formData.append('instagram_user_id', selectedAccount);
      formData.append('caption', postContent);
      formData.append('post_type', 'manual');
      formData.append('use_ai', 'false');
      
      if (postImage) {
        formData.append('image', postImage);
      }

      const response = await apiClient.createInstagramPost(formData);

      if (response.success) {
        setMessage('Instagram post created successfully!');
        setPostContent('');
        setPostImage(null);
        setAiPrompt('');
        setShowAI(false);
        
        // Refresh media after posting
        if (selectedAccount) {
          loadUserMedia(selectedAccount);
        }
      } else {
        setMessage(`Failed to create post: ${response.error || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Post creation error:', error);
      setMessage(`Error creating post: ${error.message || 'Unknown error'}`);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateAIPost = async () => {
    if (!selectedAccount || !aiPrompt.trim()) {
      setMessage('Please select an account and enter an AI prompt');
      return;
    }

    setGeneratingAI(true);
    setMessage('Generating AI content...');

    try {
      const formData = new FormData();
      formData.append('instagram_user_id', selectedAccount);
      formData.append('prompt', aiPrompt);
      formData.append('post_type', 'post-auto');
      formData.append('use_ai', 'true');
      
      if (postImage) {
        formData.append('image', postImage);
      }

      const response = await apiClient.createInstagramPost(formData);

      if (response.success) {
        setMessage('AI-generated Instagram post created successfully!');
        setPostContent('');
        setPostImage(null);
        setAiPrompt('');
        setShowAI(false);
        
        // Refresh media after posting
        if (selectedAccount) {
          loadUserMedia(selectedAccount);
        }
      } else {
        setMessage(`Failed to create AI post: ${response.error || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('AI post creation error:', error);
      setMessage(`Error creating AI post: ${error.message || 'Unknown error'}`);
    } finally {
      setGeneratingAI(false);
    }
  };

  const loadUserMedia = async (instagramUserId) => {
    setLoadingMedia(true);
    try {
      const media = await apiClient.getInstagramMedia(instagramUserId);
      setUserMedia(media || []);
    } catch (error) {
      console.error('Error loading media:', error);
      setMessage(`Error loading media: ${error.response?.data?.detail || error.message}`);
    } finally {
      setLoadingMedia(false);
    }
  };

  const handleAccountChange = (accountId) => {
    setSelectedAccount(accountId);
    setActiveTab('post');
    if (accountId) {
      loadUserMedia(accountId);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setPostImage(file);
    }
  };

  const handleLogout = () => {
    if (window.FB) {
      window.FB.logout(() => {
        setIsConnected(false);
        setInstagramAccounts([]);
        setSelectedAccount('');
        setFbAccessToken(null);
        setUserMedia([]);
        setActiveTab('connect');
        setMessage('Logged out successfully');
      });
    }
  };

  if (authLoading) {
    return (
      <div className="instagram-container">
        <div className="loading-screen">
          <div className="loading-spinner"></div>
          <p>Checking authentication...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="instagram-container">
      {/* Header */}
      <div className="instagram-header">
        <div className="header-content">
          <div className="header-left">
            <div className="logo-section">
              <div className="instagram-icon">
                <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="2" y="2" width="20" height="20" rx="5" ry="5"/>
                  <path d="m16 11.37-4-4-6 6"/>
                  <circle cx="8.5" cy="8.5" r="1.5"/>
                </svg>
              </div>
              <div>
                <h1>Instagram Manager</h1>
                <p>{isConnected ? `Connected • ${instagramAccounts.length} account(s)` : 'Not connected'}</p>
              </div>
            </div>
          </div>
          
          <div className="header-actions">
            {isConnected && (
              <button onClick={handleLogout} className="logout-btn">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"/>
                  <polyline points="16,17 21,12 16,7"/>
                  <line x1="21" y1="12" x2="9" y2="12"/>
                </svg>
                Disconnect
              </button>
            )}
            
            <button onClick={() => navigate('/')} className="back-btn">
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="m12 19-7-7 7-7"/>
                <path d="M19 12H5"/>
              </svg>
              Dashboard
            </button>
          </div>
        </div>
      </div>

      {/* Status Message */}
      {message && (
        <div className={`status-message ${message.includes('Error') || message.includes('Failed') ? 'error' : 
          message.includes('success') || message.includes('Connected') ? 'success' : 'info'}`}>
          {message}
        </div>
      )}

      {/* Main Content */}
      <div className="main-content">
        {/* Connect Section */}
        {!isConnected && (
          <div className="connect-section">
            <div className="connect-card">
              <div className="connect-icon">
                <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                  <rect x="2" y="2" width="20" height="20" rx="5" ry="5"/>
                  <path d="m16 11.37-4-4-6 6"/>
                  <circle cx="8.5" cy="8.5" r="1.5"/>
                </svg>
              </div>
              <h2>Connect Your Instagram</h2>
              <p>Connect your Instagram Business account to start creating and managing posts</p>
              
              <button 
                onClick={handleFacebookLogin} 
                disabled={!sdkLoaded}
                className="connect-button"
              >
                {!sdkLoaded ? 'Loading...' : 'Connect Instagram'}
              </button>
            </div>
          </div>
        )}

        {/* Account Selection */}
        {isConnected && activeTab === 'accounts' && (
          <div className="accounts-section">
            <h2>Select Instagram Account</h2>
            <div className="accounts-grid">
              {instagramAccounts.map((account) => (
                <div 
                  key={account.platform_id} 
                  className="account-card"
                  onClick={() => handleAccountChange(account.platform_id)}
                >
                  <div className="account-avatar">
                    {account.profile_picture ? (
                      <img src={account.profile_picture} alt="" />
                    ) : (
                      <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
                        <circle cx="12" cy="7" r="4"/>
                      </svg>
                    )}
                  </div>
                  <div className="account-info">
                    <h3>@{account.username}</h3>
                    <p>{account.display_name}</p>
                    <div className="account-stats">
                      <span>{account.followers_count} followers</span>
                      <span>{account.media_count} posts</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Post Creation */}
        {isConnected && selectedAccount && activeTab === 'post' && (
          <div className="post-section">
            <div className="post-header">
              <h2>Create Instagram Post</h2>
              <div className="post-tabs">
                <button 
                  className={`tab-btn ${!showAI ? 'active' : ''}`}
                  onClick={() => setShowAI(false)}
                >
                  Manual Post
                </button>
                <button 
                  className={`tab-btn ${showAI ? 'active' : ''}`}
                  onClick={() => setShowAI(true)}
                >
                  AI Generated
                </button>
              </div>
            </div>

            <div className="post-form">
              {!showAI ? (
                <>
                  <div className="form-group">
                    <label htmlFor="caption">Caption</label>
                    <textarea
                      id="caption"
                      value={postContent}
                      onChange={(e) => setPostContent(e.target.value)}
                      placeholder="Write your Instagram caption..."
                      rows="6"
                      className="post-textarea"
                    />
                  </div>
                </>
              ) : (
                <>
                  <div className="form-group">
                    <label htmlFor="ai-prompt">AI Prompt</label>
                    <textarea
                      id="ai-prompt"
                      value={aiPrompt}
                      onChange={(e) => setAiPrompt(e.target.value)}
                      placeholder="Describe what kind of post you want (e.g., 'motivational quote about success', 'funny cat story', 'product launch announcement')..."
                      rows="4"
                      className="post-textarea"
                    />
                  </div>
                </>
              )}
              
              <div className="form-group">
                <label htmlFor="image">Media (Image or Video)</label>
                <div className="image-upload">
                  <input
                    id="image"
                    type="file"
                    accept="image/*,video/*"
                    onChange={handleImageChange}
                    className="file-input"
                  />
                  <label htmlFor="image" className="file-label">
                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
                      <circle cx="9" cy="9" r="2"/>
                      <path d="M21 15l-3.086-3.086a2 2 0 0 0-2.828 0L6 21"/>
                    </svg>
                    {postImage ? postImage.name : 'Choose Image or Video'}
                  </label>
                </div>
              </div>

              <div className="form-actions">
                {!showAI ? (
                  <button 
                    onClick={handleCreatePost}
                    disabled={loading || (!postContent.trim() && !postImage)}
                    className="publish-button"
                  >
                    {loading ? (
                      <>
                        <div className="spinner"></div>
                        Publishing...
                      </>
                    ) : (
                      <>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <path d="M12 19V5m-7 7l7-7 7 7"/>
                        </svg>
                        Publish Post
                      </>
                    )}
                  </button>
                ) : (
                  <button 
                    onClick={handleCreateAIPost}
                    disabled={generatingAI || !aiPrompt.trim()}
                    className="publish-button ai-button"
                  >
                    {generatingAI ? (
                      <>
                        <div className="spinner"></div>
                        Generating...
                      </>
                    ) : (
                      <>
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                          <circle cx="12" cy="12" r="3"/>
                          <path d="M12 1v6m0 6v6m11-7h-6m-6 0H1"/>
                        </svg>
                        Generate & Publish
                      </>
                    )}
                  </button>
                )}
              </div>
            </div>

            {/* Recent Posts */}
            <div className="media-section">
              <div className="media-header">
                <h3>Recent Posts</h3>
                <button onClick={() => loadUserMedia(selectedAccount)} className="refresh-btn">
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M23 4v6h-6"/>
                    <path d="M20.49 15a9 9 0 1 1-2.12-9.36L23 10"/>
                  </svg>
                  Refresh
                </button>
              </div>
              
              {loadingMedia ? (
                <div className="loading-media">
                  <div className="spinner"></div>
                  <p>Loading posts...</p>
                </div>
              ) : userMedia.length > 0 ? (
                <div className="media-grid">
                  {userMedia.slice(0, 8).map((media) => (
                    <div key={media.id} className="media-item">
                      <div className="media-content">
                        {media.media_type === 'IMAGE' ? (
                          <img src={media.media_url} alt="Instagram post" />
                        ) : media.media_type === 'VIDEO' ? (
                          <video>
                            <source src={media.media_url} type="video/mp4" />
                          </video>
                        ) : null}
                      </div>
                      <div className="media-overlay">
                        <div className="media-info">
                          <p className="media-caption">
                            {media.caption ? media.caption.substring(0, 60) + '...' : 'No caption'}
                          </p>
                          <p className="media-date">
                            {new Date(media.timestamp).toLocaleDateString()}
                          </p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="no-media">
                  <p>No posts found</p>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InstagramPage;
