import React, { useState } from 'react';
import './App.css';

function App() {
  const [hoveredCard, setHoveredCard] = useState(null);

  const dashboardItems = [
    {
      id: 1,
      title: 'Social Media',
      description: 'Manage and automate your social media presence',
      icon: 'ðŸ“±',
    },
    {
      id: 2,
      title: 'Email',
      description: 'Automate email campaigns and communications',
      icon: 'ðŸ“§',
    },
    {
      id: 3,
      title: 'Advertisement',
      description: 'Optimize and manage advertising campaigns',
      icon: 'ðŸ“Š',
    }
  ];

  return (
    <div className="App">
      <header className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">
            <span className="brand-text">ThorSignia's</span>
            <span className="dashboard-text">Automation Dashboard</span>
          </h1>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="dashboard-grid">
          {dashboardItems.map((item) => (
            <div
              key={item.id}
              className={`dashboard-card ${hoveredCard === item.id ? 'hovered' : ''}`}
              onMouseEnter={() => setHoveredCard(item.id)}
              onMouseLeave={() => setHoveredCard(null)}
            >
              <div className="card-icon">{item.icon}</div>

              <div className="card-content">
                <h3 className="card-title">{item.title}</h3>
                <p className="card-description">{item.description}</p>
              </div>

              <div className="card-footer">
                <button className="card-action-btn">
                  <span>Open</span>
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <polyline points="9,18 15,12 9,6"></polyline>
                  </svg>
                </button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
