import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './SocialMediaPage.css';

function SocialMediaPage() {
  const navigate = useNavigate();
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('');
  const [hoveredCard, setHoveredCard] = useState(null);

  // Facebook OAuth states
  const [pageAccessToken, setPageAccessToken] = useState('');
  const [pages, setPages] = useState([]);
  const [selectedPage, setSelectedPage] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Auto-reply toggle state
  const [autoReplyEnabled, setAutoReplyEnabled] = useState(false);

  // Form data state - simplified to only include message
  const [formData, setFormData] = useState({
    message: '',
    image: null
  });

  // Post type selection state
  const [postType, setPostType] = useState('post-auto');

  const WEBHOOK_URL_AUTO = "https://hook.eu2.make.com/m4f3qzhwhe4y62i9ghviwe4zw23hytio";
  const WEBHOOK_URL_MANUAL = "https://hook.eu2.make.com/ax555s2navr614tpiu5jedjyn7c7tf1e";
  const WEBHOOK_URL_AUTO_REPLY = "https://hook.eu2.make.com/c3n5opodnm1d9yruq6bihlr08b22l84q"; 

  // Icon Components
  const PostTypeIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
      <polyline points="14,2 14,8 20,8"></polyline>
    </svg>
  );

  const MessageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z"></path>
    </svg>
  );

  const PageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M13 2L3 14h9l-1 8 10-12h-9l1-8z"></path>
    </svg>
  );

  const ImageIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
      <circle cx="8.5" cy="8.5" r="1.5"></circle>
      <polyline points="21,15 16,10 5,21"></polyline>
    </svg>
  );

  const AutoReplyIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M21 11.5a8.38 8.38 0 0 1-.9 3.8 8.5 8.5 0 0 1-7.6 4.7 8.38 8.38 0 0 1-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 0 1-.9-3.8 8.5 8.5 0 0 1 4.7-7.6 8.38 8.38 0 0 1 3.8-.9h.5a8.48 8.48 0 0 1 8 8v.5z"></path>
    </svg>
  );

  const LockIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="11" width="18" height="11" rx="2" ry="2"></rect>
      <circle cx="12" cy="16" r="1"></circle>
      <path d="M7 11V7a5 5 0 0 1 10 0v4"></path>
    </svg>
  );

  const RocketIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M4.5 16.5c-1.5 1.26-2 5-2 5s3.74-.5 5-2c.71-.84.7-2.13-.09-2.91a2.18 2.18 0 0 0-2.91-.09z"></path>
      <path d="M12 15l-3-3a22 22 0 0 1 2-3.95A12.88 12.88 0 0 1 22 2c0 2.72-.78 7.5-6 11a22.35 22.35 0 0 1-4 2z"></path>
      <path d="M9 12H4s.55-3.03 2-4c1.62-1.08 5 0 5 0"></path>
      <path d="M12 15v5s3.03-.55 4-2c1.08-1.62 0-5 0-5"></path>
    </svg>
  );

  const LogoutIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
      <polyline points="16,17 21,12 16,7"></polyline>
      <line x1="21" y1="12" x2="9" y2="12"></line>
    </svg>
  );

  // Clean up any existing Facebook SDK instances
  const cleanupFacebookSDK = () => {
    const existingScript = document.getElementById('facebook-jssdk');
    if (existingScript) {
      existingScript.remove();
    }

    if (window.FB) {
      delete window.FB;
    }

    if (window.fbAsyncInit) {
      delete window.fbAsyncInit;
    }
  };

  // Load Facebook SDK function
  const loadFacebookSDK = () => {
    return new Promise((resolve, reject) => {
      cleanupFacebookSDK();

      window.fbAsyncInit = function () {
        try {
          const appId = process.env.REACT_APP_FACEBOOK_APP_ID || '611792801934261';

          window.FB.init({
            appId: appId,
            cookie: true,
            xfbml: true,
            version: 'v23.0'
          });

          // Check if user is already logged in
          window.FB.getLoginStatus((response) => {
            if (response.status === 'connected') {
              fetchPages(response.authResponse.accessToken);
            }
          });

          resolve();
        } catch (error) {
          console.error('Facebook SDK initialization error:', error);
          reject(error);
        }
      };

      const script = document.createElement('script');
      script.id = 'facebook-jssdk';
      script.src = 'https://connect.facebook.net/en_US/sdk.js';
      script.async = true;
      script.defer = true;
      script.crossOrigin = 'anonymous';

      script.onerror = () => {
        reject(new Error('Failed to load Facebook SDK script'));
      };

      document.body.appendChild(script);
    });
  };

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: files ? files[0] : value
    }));
  };

  const handlePostTypeChange = (e) => {
    setPostType(e.target.value);
    // Clear image when switching away from image post types
    if (!e.target.value.includes('image')) {
      setFormData(prev => ({ ...prev, image: null }));
    }
  };

  const handleAutoReplyToggle = async () => {
    const newState = !autoReplyEnabled;
    setAutoReplyEnabled(newState);

    try {
      const payload = {
        autoReplyEnabled: newState,
        pageId: selectedPage?.id || 'user_profile',
        pageName: selectedPage?.name || 'User Profile',
        timestamp: new Date().toISOString(),
        platform: 'facebook'
      };

      await fetch(WEBHOOK_URL_AUTO_REPLY, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(payload)
      });

      setConnectionStatus(`${newState ? '✅' : '❌'} Auto-reply ${newState ? 'enabled' : 'disabled'}`);
    } catch (error) {
      console.error('Auto-reply toggle error:', error);
      setConnectionStatus('❌ Failed to update auto-reply setting');
    }
  };

  const handlePageSelection = (e) => {
    const pageId = e.target.value;
    const page = pages.find(p => p.id === pageId);
    setSelectedPage(page);
    setPageAccessToken(page.access_token);
  };

  const loginWithFacebook = async () => {
    // Check HTTPS requirement first
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost') {
      setConnectionStatus('❌ Facebook login requires HTTPS. Please use https://localhost:3001 or deploy with HTTPS');
      return;
    }

    setIsConnecting(true);
    setConnectionStatus('📦 Loading Facebook SDK...');

    try {
      await loadFacebookSDK();

      if (!window.FB || typeof window.FB.login !== 'function') {
        setConnectionStatus('❌ Facebook SDK failed to load. Please refresh the page and try again.');
        setIsConnecting(false);
        return;
      }

      setConnectionStatus('🔐 Connecting to Facebook...');

      window.FB.login((response) => {
        if (response.authResponse) {
          setConnectionStatus('✅ Facebook login successful!');
          fetchPages(response.authResponse.accessToken);
        } else if (response.status === 'not_authorized') {
          setConnectionStatus('❌ Please authorize the app to continue. Check Facebook App settings.');
          setIsConnecting(false);
        } else if (response.status === 'unknown') {
          setConnectionStatus('❌ Facebook app configuration error. Please check your Facebook App settings.');
          setIsConnecting(false);
        } else {
          setConnectionStatus('❌ Facebook login cancelled or failed');
          setIsConnecting(false);
        }
      }, {
        scope: 'public_profile'
      });
    } catch (error) {
      console.error('Facebook login error:', error);

      if (error.message && error.message.includes('http pages')) {
        setConnectionStatus('❌ Facebook requires HTTPS. Please use HTTPS or localhost');
      } else if (error.message && error.message.includes('init not called')) {
        setConnectionStatus('❌ Facebook SDK initialization failed. Please refresh the page and try again.');
      } else {
        setConnectionStatus('❌ Facebook login failed: ' + error.message);
      }
      setIsConnecting(false);
    }
  };

  const fetchPages = (userAccessToken) => {
    setConnectionStatus('📄 Fetching your Facebook pages...');

    try {
      window.FB.api('/me/accounts', 'GET', { access_token: userAccessToken }, (response) => {
        if (response.error) {
          console.error('Facebook API error:', response.error);
          setConnectionStatus(`❌ Facebook API error: ${response.error.message}`);
          setIsConnecting(false);
          return;
        }

        if (response.data && response.data.length > 0) {
          setPages(response.data);
          const firstPage = response.data[0];
          setSelectedPage(firstPage);
          setPageAccessToken(firstPage.access_token);
          setIsAuthenticated(true);
          setConnectionStatus(`✅ Connected! Found ${response.data.length} page(s)`);
        } else {
          setConnectionStatus('✅ Facebook connected successfully!');
          setIsAuthenticated(true);
          setPages([]);
          setSelectedPage(null);
          setPageAccessToken(userAccessToken);
        }
        setIsConnecting(false);
      });
    } catch (error) {
      console.error('Fetch pages error:', error);
      setConnectionStatus('❌ Failed to fetch Facebook pages');
      setIsConnecting(false);
    }
  };

  const handleFacebookConnect = async () => {
    if (!isAuthenticated) {
      loginWithFacebook();
      return;
    }

    // Validate required fields
    if (!formData.message || formData.message.trim() === '') {
      setConnectionStatus('❌ Please enter a message');
      return;
    }

    if (!pageAccessToken) {
      setConnectionStatus('❌ No Facebook access token available');
      return;
    }

    // Validate image for image post types
    if (postType.includes('image') && !formData.image) {
      setConnectionStatus('❌ Please select an image for image posts');
      return;
    }

    try {
      setIsConnecting(true);
      setConnectionStatus('🚀 Posting to Facebook...');

      const webhookUrl = postType === 'post-manual' ? WEBHOOK_URL_MANUAL : WEBHOOK_URL_AUTO;

      const payload = {
        pageAccessToken,
        pageId: selectedPage?.id || 'user_profile',
        pageName: selectedPage?.name || 'User Profile',
        message: formData.message,
        postType: postType,
        timestamp: new Date().toISOString(),
        platform: 'facebook',
        authenticated: true,
        isUserProfile: !selectedPage,
        autoReplyEnabled: autoReplyEnabled
      };

      // Handle image upload if present
      if (formData.image) {
        const formDataWithImage = new FormData();
        formDataWithImage.append('image', formData.image);
        formDataWithImage.append('data', JSON.stringify(payload));

        const webhookResponse = await fetch(webhookUrl, {
          method: "POST",
          body: formDataWithImage
        });

        setConnectionStatus(webhookResponse.ok ? '✅ Image post uploaded successfully!' : '❌ Failed to upload image post');
      } else {
        // Regular text post
        const webhookResponse = await fetch(webhookUrl, {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(payload)
        });

        setConnectionStatus(webhookResponse.ok ? '✅ Message posted to Facebook successfully!' : '❌ Failed to post to Facebook');
      }

      // Reset form after successful submission
      setFormData({
        message: '',
        image: null
      });
    } catch (error) {
      console.error('Webhook error:', error);
      setConnectionStatus('❌ Connection error occurred');
    } finally {
      setIsConnecting(false);
    }
  };

  const handleInstagramConnect = () => {
    setConnectionStatus('🚧 Instagram integration coming soon!');
  };

  const handleLogout = () => {
    if (window.FB) {
      window.FB.logout(() => {
        setIsAuthenticated(false);
        setPages([]);
        setSelectedPage(null);
        setPageAccessToken('');
        setConnectionStatus('👋 Logged out from Facebook');
      });
    } else {
      setIsAuthenticated(false);
      setPages([]);
      setSelectedPage(null);
      setPageAccessToken('');
      setConnectionStatus('👋 Logged out from Facebook');
    }
  };

  const platforms = [
    {
      id: 'facebook',
      name: 'Facebook',
      description: isAuthenticated ? `Connected as ${selectedPage?.name || 'Facebook User'}` : 'Not Connected',
      hasForm: true,
      icon: (
        <svg width="64" height="64" viewBox="0 0 24 24" fill="#1877F2">
          <path d="M24 12.073c0-6.627-5.373-12-12-12s-12 5.373-12 12c0 5.99 4.388 10.954 10.125 11.854v-8.385H7.078v-3.47h3.047V9.43c0-3.007 1.792-4.669 4.533-4.669 1.312 0 2.686.235 2.686.235v2.953H15.83c-1.491 0-1.956.925-1.956 1.874v2.25h3.328l-.532 3.47h-2.796v8.385C19.612 23.027 24 18.062 24 12.073z"/>
        </svg>
      ),
      connectHandler: handleFacebookConnect,
      isConnecting: isConnecting,
      gradient: 'linear-gradient(135deg, #1877F2 0%, #42a5f5 100%)',
      borderColor: '#1877F2'
    },
    {
      id: 'instagram',
      name: 'Instagram',
      description: 'Not Connected',
      hasForm: false,
      icon: (
        <svg width="64" height="64" viewBox="0 0 24 24" fill="url(#instagram-gradient)">
          <defs>
            <linearGradient id="instagram-gradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#405DE6"/>
              <stop offset="25%" stopColor="#5851DB"/>
              <stop offset="50%" stopColor="#833AB4"/>
              <stop offset="75%" stopColor="#C13584"/>
              <stop offset="100%" stopColor="#E1306C"/>
            </linearGradient>
          </defs>
          <path d="M12 2.163c3.204 0 3.584.012 4.85.07 3.252.148 4.771 1.691 4.919 4.919.058 1.265.069 1.645.069 4.849 0 3.205-.012 3.584-.069 4.849-.149 3.225-1.664 4.771-4.919 4.919-1.266.058-1.644.07-4.85.07-3.204 0-3.584-.012-4.849-.07-3.26-.149-4.771-1.699-4.919-4.92-.058-1.265-.07-1.644-.07-4.849 0-3.204.013-3.583.07-4.849.149-3.227 1.664-4.771 4.919-4.919 1.266-.057 1.645-.069 4.849-.069zm0-2.163c-3.259 0-3.667.014-4.947.072-4.358.2-6.78 2.618-6.98 6.98-.059 1.281-.073 1.689-.073 4.948 0 3.259.014 3.668.072 4.948.2 4.358 2.618 6.78 6.98 6.98 1.281.058 1.689.072 4.948.072 3.259 0 3.668-.014 4.948-.072 4.354-.2 6.782-2.618 6.979-6.98.059-1.28.073-1.689.073-4.948 0-3.259-.014-3.667-.072-4.947-.196-4.354-2.617-6.78-6.979-6.98-1.281-.059-1.69-.073-4.949-.073zm0 5.838c-3.403 0-6.162 2.759-6.162 6.162s2.759 6.163 6.162 6.163 6.162-2.759 6.162-6.163c0-3.403-2.759-6.162-6.162-6.162zm0 10.162c-2.209 0-4-1.79-4-4 0-2.209 1.791-4 4-4s4 1.791 4 4c0 2.21-1.791 4-4 4zm6.406-11.845c-.796 0-1.441.645-1.441 1.44s.645 1.44 1.441 1.44c.795 0 1.439-.645 1.439-1.44s-.644-1.44-1.439-1.44z"/>
        </svg>
      ),
      connectHandler: handleInstagramConnect,
      isConnecting: false,
      gradient: 'linear-gradient(135deg, #405DE6 0%, #5851DB 25%, #833AB4 50%, #C13584 75%, #E1306C 100%)',
      borderColor: '#E1306C'
    }
  ];

  return (
    <div className="page-container">
      <header className="page-header">
        <button onClick={() => navigate('/')} className="back-btn">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <polyline points="15,18 9,12 15,6"></polyline>
          </svg>
          Back to Dashboard
        </button>
        <div className="page-title-section">
          <h1 className="page-title">Social Media Automation</h1>
          <p className="page-subtitle">Connect and manage your social media platforms</p>
        </div>
      </header>

      <main className="page-main">
        <div className="platforms-container">
          <div className="platforms-grid">
            {platforms.map((platform) => (
              <div
                key={platform.id}
                className={`platform-card ${hoveredCard === platform.id ? 'hovered' : ''} ${platform.id === 'facebook' && isAuthenticated ? 'authenticated' : ''}`}
                onMouseEnter={() => setHoveredCard(platform.id)}
                onMouseLeave={() => setHoveredCard(null)}
                style={{
                  '--platform-gradient': platform.gradient,
                  '--platform-border': platform.borderColor
                }}
              >
                <div className="platform-icon">
                  {platform.icon}
                </div>

                <div className="platform-content">
                  <h3 className="platform-name">{platform.name}</h3>
                  <p className="platform-description">{platform.description}</p>

                  {platform.hasForm && isAuthenticated ? (
                    <div className="platform-form">
                      {/* Auto-reply toggle */}
                      <div className="form-group auto-reply-group">
                        <label className="form-label toggle-label">
                          <AutoReplyIcon />
                          Auto-reply to comments
                          <div className="toggle-switch">
                            <input
                              type="checkbox"
                              checked={autoReplyEnabled}
                              onChange={handleAutoReplyToggle}
                              className="toggle-input"
                            />
                            <span className="toggle-slider"></span>
                          </div>
                        </label>
                      </div>

                      <div className="form-group">
                        <label className="form-label">
                          <PostTypeIcon />
                          Post Type
                        </label>
                        <div className="custom-select-wrapper">
                          <select
                            value={postType}
                            onChange={handlePostTypeChange}
                            className="glass-input glass-select custom-select"
                          >
                            <option value="post-auto">Write with AI</option>
                            <option value="post-manual">Write Manually</option>
                            <option value="image-post-auto">Image post with AI</option>
                            <option value="image-post-manual">Image post Manually</option>
                          </select>
                          <div className="select-arrow">
                            <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                              <polyline points="6,9 12,15 18,9"></polyline>
                            </svg>
                          </div>
                        </div>
                      </div>

                      {/* Page Selection - Always show when authenticated */}
                      <div className="form-group">
                        <label className="form-label">
                          <PageIcon />
                          Page Selection
                        </label>
                        {pages.length > 0 ? (
                          <div className="custom-select-wrapper">
                            <select
                              value={selectedPage?.id || ''}
                              onChange={handlePageSelection}
                              className="glass-input glass-select custom-select"
                            >
                              {pages.map(page => (
                                <option key={page.id} value={page.id}>
                                  {page.name} ({page.category || 'Page'})
                                </option>
                              ))}
                            </select>
                            <div className="select-arrow">
                              <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <polyline points="6,9 12,15 18,9"></polyline>
                              </svg>
                            </div>
                          </div>
                        ) : (
                          <div className="page-info-display">
                            <div className="page-info-item">
                              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                                <circle cx="12" cy="7" r="4"></circle>
                              </svg>
                              <span>Connected as User Profile</span>
                            </div>
                            <p className="page-info-note">
                              No business pages found. You can still post to your personal profile or create a business page on Facebook.
                            </p>
                          </div>
                        )}
                      </div>

                      {/* Image upload field for manual image posts */}
                      {postType === 'image-post-manual' && (
                        <div className="form-group">
                          <label className="form-label">
                            <ImageIcon />
                            Upload Image
                          </label>
                          <div className="image-upload-wrapper">
                            <input
                              type="file"
                              name="image"
                              accept="image/*"
                              onChange={handleInputChange}
                              className="image-input"
                              id="image-upload"
                            />
                            <label htmlFor="image-upload" className="image-upload-label">
                              <ImageIcon />
                              {formData.image ? formData.image.name : 'Choose an image'}
                            </label>
                          </div>
                        </div>
                      )}

                      <div className="form-group">
                        <label className="form-label">
                          <MessageIcon />
                          Your Message
                        </label>
                        <textarea
                          name="message"
                          placeholder="What's on your mind? Share your thoughts with the world..."
                          value={formData.message}
                          onChange={handleInputChange}
                          className="glass-input glass-textarea enhanced-textarea"
                          rows="4"
                        />
                      </div>
                    </div>
                  ) : platform.hasForm ? (
                    <div className="auth-prompt">
                      <p className="auth-description">
                        <LockIcon />
                        Please authenticate with Facebook to start posting
                      </p>
                    </div>
                  ) : null}
                </div>

                <div className="platform-actions">
                  {platform.id === 'facebook' && isAuthenticated ? (
                    <div className="auth-actions">
                      <button 
                        className="connect-btn"
                        onClick={platform.connectHandler}
                        disabled={platform.isConnecting}
                      >
                        {platform.isConnecting ? (
                          <>
                            <div className="loading-spinner" />
                            Posting...
                          </>
                        ) : (
                          <>
                            <RocketIcon />
                            Post to Facebook
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                              <polyline points="9,18 15,12 9,6"></polyline>
                            </svg>
                          </>
                        )}
                      </button>
                      <button 
                        className="logout-btn"
                        onClick={handleLogout}
                      >
                        <LogoutIcon />
                        Logout
                      </button>
                    </div>
                  ) : (
                    <button 
                      className="connect-btn"
                      onClick={platform.connectHandler}
                      disabled={platform.isConnecting}
                    >
                      {platform.isConnecting ? (
                        <>
                          <div className="loading-spinner" />
                          Connecting...
                        </>
                      ) : (
                        <>
                          {platform.hasForm ? (
                            <>
                              <LockIcon />
                              Connect Facebook
                            </>
                          ) : (
                            '🔗 Connect'
                          )}
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                            <polyline points="9,18 15,12 9,6"></polyline>
                          </svg>
                        </>
                      )}
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>

          {connectionStatus && (
            <div className="connection-status">
              <div className={`status-message ${connectionStatus.includes('✅') ? 'success' : connectionStatus.includes('🚧') ? 'info' : 'error'}`}>
                {connectionStatus}
              </div>
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default SocialMediaPage;
